    @cancel="() => { $emit('cancel') }"
    ok-text="提交"
    cancel-text="取消"
    :keyboard="false"
    :maskClosable="false"
  >
    <div v-if="mode==='delay'">
      <a-descriptions :column="1" style="margin-left: 40px">
        <a-descriptions-item label="延期原因">{{ delayInfo.delayReason }}</a-descriptions-item>
