        <a-radio-group v-decorator="['status', { rules: [{ required: true, message: '请选择审批意见'}]}]">
          <a-radio value="1">通过</a-radio>
          <a-radio value="2">驳回</a-radio>
        </a-radio-group>
      </a-form-item>
      <a-form-item label="审批意见">
        <a-textarea :rows="4" v-decorator="['comment', { rules: [{ required: true, message: '请填写审批意见'}]}]"></a-textarea>
      </a-form-item>
    </a-form>
  </a-modal>
</template>

<script>
export default {
  name: 'ApproveModal',
  props: {
    visible: {
      type: Boolean,
      default: false
    },
    delayInfo: {
      type: Object,
      default: () => null
    },
    mode: {
      type: String,
      default: ''
    }
  },
  data () {
    return {
      form: this.$form.createForm(this)
    }
  }
}
</script>

