<template>
  <a-modal
    :title="modalTitle"
    :width="800"
    :visible="visible"
    :confirmLoading="loading"
    :footer="null"
    @ok="() => { $emit('ok') }"
    @cancel="() => { $emit('cancel') }"
    :keyboard="false"
    :maskClosable="false"
  >
    <a-spin :spinning="loading">

      <jsx-preview :model="formData" @submit="close"/>

    </a-spin>
  </a-modal>
</template>

<script>
import jsxPreview from '@/views/safety/jsxPreview'
import { workPlan } from '@/api/safety'

export default {
  name: 'WorkPlanModal',
  components: {
    jsxPreview
  },
  props: {
    visible: {
      type: Boolean,
      required: true
    },
    loading: {
      type: Boolean,
      default: () => false
    },
    model: {
      type: Object,
      default: () => null
    },
    workTypeOptions: {
      type: Object,
      default: () => null
    },
    inspectionTypeOptions: {
      type: Object,
      default: () => null
    },
    executeTypeOptions: {
      type: Object,
      default: () => null
    },
    modalTitle: {
      type: String,
      default: '操作'
    },
    disabled: {
      type: Boolean,
      default: false
    },
    mode: {
      type: String,
      default: 'work'
    }
  },
  data () {
    return {
      formData: {}
    }
  },
  created () {
    workPlan.getWorkPlanService().then(res => {
      var service = res.data
      workPlan.getFlowTypeList({ service: service }).then(res => {
        var typeList = res.data
        const item = typeList.find(item => item.name === '安全计划')
        console.log(item)
        if (item) {
          workPlan.getWorkPlanProcessList({ service: service, type: item.id }).then(res => {
            var process = res.data.slice(-1)
            if (!process.length) return
            workPlan.getWorkPlanProcessForm({ processId: process[0].id }).then(res => {
              this.formData = res.data
              this.formData.fields.map(item => {
                item.vModel = item.columnName // form表单以vModel值作为表单key
              })
            })
          })
        }
      })
    })
  },

  methods: {
    close () {
      console.log('+++++++++ok')
      this.$emit('cancel')
    }
  }
}
</script>
