<template>
  <page-container :title="false">
    <a-card :bordered="false">
      <div class="table-page-search-wrapper">
        <a-form layout="inline">
          <a-row :gutter="24">
            <a-col :md="4">
              <a-form-item label="关键字">
                <a-input v-model="queryParam.blurry"/>
              </a-form-item>
            </a-col>
            <a-col :md="4">
              <a-form-item label="检查类型">
                <a-select v-model="queryParam.examineType">
                  <a-select-option v-for="d in inspectionOptions" :key="d.dictKey">
                    {{ d.dictValue }}
                  </a-select-option>
                </a-select>
              </a-form-item>
            </a-col>
            <a-col :md="6">
              <a-form-item label="创建时间">
                <a-range-picker v-model="queryParam.createTimeList" :valueFormat="dateFormat"/>
              </a-form-item>
            </a-col>
            <a-col :md="2">
              <span class="table-page-search-submitButtons">
                <a-button type="primary" @click="inspectionPlanResearch">查询</a-button>
                <a-button type="second" style="margin-left: 10px" @click="inspectionPlanRefresh">重置</a-button>
              </span>
            </a-col>
          </a-row>
        </a-form>
      </div>
      <div class="table-operator">
        <a-button type="primary" icon="plus" @click="planAdd">新增</a-button>
        <!--        <a-button icon="delete" :disabled="!hasSelected" @click="planRemove">删除</a-button>-->
      </div>
      <s-table
        ref="inspectionPlanTable"
        size="default"
        :scroll="{y: 'calc(100vh - 360px)',x: 1000}"
        :rowKey="record => record.id"
        :columns="columns"
        :data="loadData"
        :alert="false"
        :rowSelection="false"
        showPagination="true"
      >
        <span slot="statusName" slot-scope="text, record">
          <a-tag :color="record.status===2 || record.status===5?'red':'green'">{{ text }}</a-tag>
        </span>
        <span slot="action" slot-scope="text, record, index" v-if="tableType === 'all'">
          <template>
            <!--            <a-config-provider :auto-insert-space-in-button="false" style="margin-left: 4px;margin-right: 4px">-->
            <!--              <a-button type="primary" size="small" @click="planCheck(record)"><a-icon type="file-search"/> 查看</a-button>-->
            <!--            </a-config-provider>-->
            <!--            <a-config-provider :auto-insert-space-in-button="false" style="margin-left: 4px;margin-right: 4px" v-if="userInfo.id === record.chargerId && [1].includes(record.status)">-->
            <!--              <a-button type="danger" size="small" @click="planRecord(record)"><a-icon type="file-text"/>记录</a-button>-->
            <!--            </a-config-provider>-->
            <!--            <a-config-provider :auto-insert-space-in-button="false" style="margin-left: 4px;margin-right: 4px">-->
            <!--              <a-button type="primary" size="small" @click="planEdit(record, index)"><a-icon type="edit"/>编辑</a-button>-->
            <!--            </a-config-provider>-->
            <a @click="planCheck(record)">查看</a>
            <a-divider type="vertical"/>
            <a @click="planEdit(record, index)">编辑</a>
          </template>
        </span>
        <span slot="action" slot-scope="text, record, index" v-else-if="tableType === 'todo'">
          <template>
            <a-config-provider :auto-insert-space-in-button="false" style="margin-left: 4px;margin-right: 4px" v-if="[0].includes(record.status)">
              <a-button type="danger" size="small" @click="planApprove(record, index)"><a-icon type="user"/>审批</a-button>
            </a-config-provider>
            <a-config-provider :auto-insert-space-in-button="false" style="margin-left: 4px;margin-right: 4px" v-if="userInfo.id === record.creatorId && [0].includes(record.status)">
              <a-button type="danger" size="small" @click="planRevoke(record, index)"><a-icon type="file-text"/>撤回</a-button>
            </a-config-provider>
            <a-config-provider :auto-insert-space-in-button="false" style="margin-left: 4px;margin-right: 4px" v-if="[2,6].includes(record.status)">
              <a-button type="primary" size="small" @click="planEdit(record, index)"><a-icon type="edit"/>编辑</a-button>
            </a-config-provider>
            <!-- <a-config-provider :auto-insert-space-in-button="false" style="margin-left: 4px;margin-right: 4px" v-if="[1].includes(record.status)">
              <a-button type="danger" size="small" @click="inspectionPlanRecord(record)"><a-icon type="file-text"/>记录</a-button>
            </a-config-provider> -->
            <!-- <a-config-provider :auto-insert-space-in-button="false" style="margin-left: 4px;margin-right: 4px" v-if="[2,3,4].includes(record.status)">
              <a-button type="danger" size="small" @click="inspectionPlanStop(record)"><a-icon type="file-text"/>停用</a-button>
            </a-config-provider> -->
            <a-config-provider :auto-insert-space-in-button="false" style="margin-left: 4px;margin-right: 4px">
              <a-button type="primary" size="small" @click="planCheck(record)"><a-icon type="file-search"/> 查看</a-button>
            </a-config-provider>
            <a-config-provider :auto-insert-space-in-button="false" style="margin-left: 4px;margin-right: 4px">
              <a-button style="background-color: #0BB7AF;color: #fdfdfd" size="small" @click="flowchartShow(record, index)"><a-icon type="share-alt"/>流程示意</a-button>
            </a-config-provider>
          </template>
        </span>
        <span slot="action" slot-scope="text, record, index" v-else>
          <template>
            <!--            <a-config-provider :auto-insert-space-in-button="false" style="margin-left: 4px;margin-right: 4px">-->
            <!--              <a-button type="primary" size="small" @click="planCheck(record)"><a-icon type="file-search"/> 查看</a-button>-->
            <!--            </a-config-provider>-->
            <!--            <a-config-provider :auto-insert-space-in-button="false" style="margin-left: 4px;margin-right: 4px" v-if="userInfo.id === record.creatorId && [0].includes(record.status)">-->
            <!--              <a-button type="danger" size="small" @click="planRevoke(record, index)"><a-icon type="file-text"/>撤回</a-button>-->
            <!--            </a-config-provider>-->
            <a @click="planCheck(record)">查看</a>
            <a-divider type="vertical"/>
            <a @click="planRevoke(record, index)">撤回</a>
          </template>
        </span>
      </s-table>
      <plan-modal
        ref="inspectionPlanModal"
        mode="inspection"
        :modal-title="commonTitle"
        :visible="commonVisible"
        :disabled="commonDisabled"
        :model="commonData"
        :inspection-type-options="inspectionOptions"
        :execute-type-options="executeTypeOptions"
        @cancel="planCancel"
        @ok="planOk"
      />
      <flow-chart
        ref="flowchart"
        :model="processDataList[processDataIndex]"
        :visible="flowChartVisible"
        @cancel="flowchartCancel"
      ></flow-chart>
      <approve-modal
        ref="approveModal"
        :visible="approveVisible"
        @cancel="approveCancel"
        @ok="approveOk"
      />
      <record-modal
        ref="recordModal"
        modal-title="执行记录填报"
        mode="inspection"
        :visible="recordVisible"
        :examine-type-options="executeTypeOptions"
        @ok="recordOk"
        @cancel="recordCancel"
      ></record-modal>
      <detail-modal
        ref="detailModal"
        :mode="detailMode"
        :visible="detailVisible"
        :basic-info="detailBasicInfo"
        @cancel="detailCancel"
      />
    </a-card>
  </page-container>
</template>
<script>
import { STable } from '@/components'
import { inspectionPlan, getInspectionType, getOriPlanExecuteType, submitInspectionRecordList } from '@/api/safety'
import { inspectionPlanColumns } from '@/views/safety/table'
import PlanModal from '@/views/safety/schedule/component/PlanModal'
import FlowChart from '@/views/safety/schedule/component/FlowChart'
import ApproveModal from '@/views/safety/schedule/component/ApproveModal'
import RecordModal from '@/views/safety/schedule/component/RecordModal'
import DetailModal from '@/views/safety/schedule/component/DetailModal'
import { getStore } from '@/utils/store'

export default {
  components: {
    STable,
    PlanModal,
    FlowChart,
    ApproveModal,
    RecordModal,
    DetailModal
  },
  data () {
    return {
      dateFormat: 'YYYY-MM-DD HH:mm:ss',
      queryParam: {},
      selectedRowKeys: [],
      selectedRows: [],
      loadData: parameter => {
        const requestParameters = { current: parameter.pageNo, size: parameter.pageSize }
        this.queryParam.createTimeList && (this.queryParam.createTimeList = typeof this.queryParam.createTimeList === 'string' ? this.queryParam.createTimeList : this.queryParam.createTimeList.join())
        Object.assign(requestParameters, this.queryParam)
        if (this.tableType === 'all') {
          return inspectionPlan.getInspectionPlanList(requestParameters).then(res => {
            return {
              totalCount: res.data.total,
              totalPage: res.data.pages,
              pageNo: res.data.current,
              pageSize: res.data.size,
              data: res.data.records
            }
          })
        } else if (this.tableType === 'todo') {
          return inspectionPlan.getInspectionPlanTodoList(requestParameters).then(res => {
            this.processDataList = res.data.records
            const inspectionPlanData = res.data.records.map(item => {
              return item.inspectionPlanVO
            })
            return {
              totalCount: res.data.total,
              totalPage: res.data.pages,
              pageNo: res.data.current,
              pageSize: res.data.size,
              data: inspectionPlanData
            }
          })
        } else {
          return inspectionPlan.getInspectionPlanSendList(requestParameters).then(res => {
            this.processDataList = res.data.records
            const inspectionPlanData = res.data.records.map(item => {
              return item.inspectionPlanVO
            })
            return {
              totalCount: res.data.total,
              totalPage: res.data.pages,
              pageNo: res.data.current,
              pageSize: res.data.size,
              data: inspectionPlanData
            }
          })
        }
      },
      // select options
      inspectionOptions: [],
      executeTypeOptions: [],
      // modal
      commonDisabled: false,
      commonTitle: '',
      commonVisible: false,
      commonData: {},
      commonMode: true, // add：true edit：false
      // switch
      switchShow: true,
      // flowchart
      flowChartVisible: false,
      // approve
      approveVisible: false,
      // record
      recordVisible: false,
      tableType: 'all',
      processDataList: [], // 记录工作流相关信息
      processDataIndex: 0, // 记录要操作的工作流index
      userInfo: getStore({ name: 'info' }),
      isAdmin: getStore({ name: 'info' }).roleId === '1330762984195112961',
      // detail
      detailVisible: false,
      detailBasicInfo: {},
      detailMode: 'inspectionPlan'
    }
  },
  created () {
    this.selectOptionsInit()
  },
  computed: {
    columns: () => inspectionPlanColumns,
    hasSelected () {
      return this.selectedRowKeys.length > 0
    },
    rowSelection () {
      return {
        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange
      }
    }
  },
  methods: {
    selectOptionsInit () {
      getInspectionType().then(res => {
        this.inspectionOptions = res.data
      })
      getOriPlanExecuteType().then(res => {
        this.executeTypeOptions = res.data
      })
    },
    modalInit () {
      this.commonTitle = ''
      this.commonDisabled = false
      this.commonData = {}
      this.commonVisible = false
      this.$refs.inspectionPlanModal.form.resetFields()
      this.$refs.recordModal.form.resetFields()
    },
    onSelectChange (selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    planResearch () {
      this.$refs.inspectionPlanTable.refresh(true)
      this.$refs.inspectionPlanTable.clearSelected()
    },
    planRefresh () {
      this.queryParam = {}
      this.$refs.inspectionPlanTable.refresh(true)
      this.$refs.inspectionPlanTable.clearSelected()
    },
    planAdd () {
      this.commonTitle = '新增计划'
      this.commonVisible = true
      this.commonMode = true
    },
    planRemove () {
      const ids = this.selectedRows.map(item => {
        return item.id
      })
      this.removeConfirm(ids)
    },
    removeConfirm (ids) {
      const _this = this
      this.$confirm({
        title: '提示',
        content: '将删除选中数据，是否确认？',
        okText: '确定',
        okType: 'danger',
        cancelText: '取消',
        onOk () {
         inspectionPlan.removeInspectionPlanList(ids).then(res => {
            if (res.code === 200) {
              _this.$message.success('删除成功')
              _this.$refs.inspectionPlanTable.refresh(true)
              _this.$refs.inspectionPlanTable.clearSelected()
            } else {
              _this.$message.success('删除失败')
            }
          })
        }
      })
    },
    planCheck (record) {
      // this.commonTitle = '查看计划'
      // this.commonData = { ...record }
      // this.commonDisabled = true
      // this.commonVisible = true
      this.detailBasicInfo = { ...record }
      this.detailVisible = true
      // inspectionPlan.detailInspectionPlanList(record.id).then(res => {
      //   if (res.code === 200) {
      //     this.detailBasicInfo = { ...res.data }
      //     this.detailVisible = true
      //   }
      // })
    },
    planEdit (record, index) {
      this.commonTitle = '编辑'
      this.commonData = record
      this.processDataIndex = index
      this.commonVisible = true
      this.commonMode = false
    },
    planOk () {
      if (this.commonDisabled) {
        this.$message.success('查看返回')
        this.commonInit()
        return
      }
      const _this = this
      const form = this.$refs.inspectionPlanModal.form
      form.validateFields((errors, values) => {
        if (!errors) {
            if (!_this.commonMode) {
              //  编辑接口
              values.id = _this.commonData.id
              // var params = {}
              // var processData = this.processDataList[this.processDataIndex]
              // params.businessId = _this.commonData.id
              // params.taskId = processData.taskId
              // params.processInstanceId = processData.processInstanceId
              // params.variables = { status: 0 }
              // params.inspectionPlan = values
              inspectionPlan.submitInspectionPlanList(values).then(res => {
                if (res.code === 200) {
                  _this.$message.success(`${res.msg}`)
                  _this.modalInit()
                  _this.$refs.inspectionPlanTable.refresh(true)
                } else {
                  _this.$message.error(`${res.msg}`)
                }
              }).catch(error => {
                _this.$message.error(`${error.response.data.msg}`)
              })
            } else {
              var requestForm = {}
              requestForm.processDefinitionKey = 'inspection_plan' // 新增工作计划并工作流字段
              requestForm.inspectionPlan = values
              inspectionPlan.submitInspectionPlanList(requestForm).then(res => {
                if (res.code === 200) {
                  _this.$message.success(`${res.msg}`)
                  _this.modalInit()
                  _this.$refs.inspectionPlanTable.refresh(true)
                  _this.$refs.inspectionPlanTable.clearSelected()
                } else {
                  _this.$message.error(`${res.msg}`)
                }
              })
            }
        }
      })
    },
    planCancel () {
      this.modalInit()
    },
    flowchartShow (record, index) {
      this.flowChartVisible = true
      this.processDataIndex = index
    },
    flowchartCancel () {
      this.flowChartVisible = false
    },
    planRecord (record) {
      this.commonData = record
      this.recordVisible = true
    },
    recordOk () {
      const _this = this
      const form = this.$refs.recordModal.form
      form.validateFields((errors, values) => {
        if (!errors) {
          values.workId = this.commonData.id
          submitInspectionRecordList(values).then(res => {
            if (res.code === 200) {
              _this.$message.success(`${res.msg}`)
              this.modalInit()
            } else {
              _this.$message.error(`${res.msg}`)
            }
          })
        }
      })
    },
    recordCancel () {
      this.modalInit()
    },
    planApprove (record, index) {
      this.processDataIndex = index
      this.approveVisible = true
      this.commonData = { ...record }
    },
    approveCancel () {
      this.$refs.approveModal.form.resetFields()
      this.approveVisible = false
    },
    approveOk () {
      const _this = this
      const form = this.$refs.approveModal.form
      form.validateFields((errors, values) => {
        if (!errors) {
          var params = {}
          var processData = this.processDataList[this.processDataIndex]
          params.businessId = this.commonData.id
          params.taskId = processData.taskId
          params.processInstanceId = processData.processInstanceId
          params.comment = values.comment
          params.variables = { status: Number(values.status) }
          if (values.status === '2') {
            params.variables.skip = false
          }
          inspectionPlan.approveInspectionPlan(params).then(res => {
            if (res.code === 200) {
              _this.$message.success(`${res.msg}`)
              _this.modalInit()
              _this.$refs.inspectionPlanTable.refresh(true)
              _this.$refs.approveModal.form.resetFields()
              _this.approveVisible = false
            } else {
              _this.$message.error(`${res.msg}`)
            }
          })
        }
      })
    },
    planRevoke (record, index) {
      this.commonData = { ...record }
      var processData = this.processDataList[index]
      const _this = this
      this.$confirm({
        title: '提示',
        content: '将撤回该工作计划，是否确认？',
        okText: '确定',
        okType: 'danger',
        cancelText: '取消',
        onOk () {
          var params = {}
          params.businessId = _this.commonData.id
          params.taskId = processData.taskId
          params.processInstanceId = processData.processInstanceId
          params.comment = '撤回申请'
          inspectionPlan.revokeInspectionPlan(params).then(res => {
            if (res.code === 200) {
              _this.$message.success(`${res.msg}`)
              _this.modalInit()
              _this.$refs.inspectionPlanTable.refresh(true)
              _this.$refs.approveModal.form.resetFields()
              _this.approveVisible = false
            } else {
              _this.$message.error(`${res.msg}`)
            }
          })
        }
      })
    },
    planStop () {
      const _this = this
      this.$confirm({
        title: '提示',
        content: '将停用该工作计划并送审，是否确认？',
        okText: '确定',
        okType: 'danger',
        cancelText: '取消',
        onOk () {
          // inspectionPlan.removeinspectionPlanList(ids).then(res => {
          //   if (res.code === 200) {
          //     _this.$message.success('删除成功')
          //     _this.$refs.inspectionPlanTable.refresh(true)
          //     _this.$refs.inspectionPlanTable.clearSelected()
          //   } else {
          //     _this.$message.success('删除失败')
          //   }
          // })
        }
      })
    },
    detailCancel () {
      this.detailVisible = false
      this.detailBasicInfo = {}
    }
  }
}
</script>
<style lang="less" scoped>
/deep/ .ant-table {
  height: calc(100vh - 288px);
}
/deep/ .ant-card-body {
  .ant-table-wrapper {
    min-height: calc(100vh - 225px);
  }
}
</style>
