<template>
  <div>
    <a-card title="基本信息" style="margin: 15px;">
      <a-form class="ant-advanced-search-form">
        <a-row :gutter="24">
          <a-col :span="10" :offset="1">
            <a-form-item label="作业证编号">
              <a-input :value="formData.code" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="管线输送介质-温度">
              <a-input :value="formData.field3" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="申请单位">
              <a-input :value="formData.applicantUnitName" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="管线输送介质-压力">
              <a-input :value="formData.field4" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="申请人">
              <a-input :value="formData.applicantName" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="盲板规格-直径(mm)">
              <a-input :value="formData.field5" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="作业单位">
              <a-input :value="formData.workUnitName" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="盲板规格-材质">
              <a-input :value="formData.field6" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="现场监护人">
              <div class="flex-input-button">
                <a-input :value="formData.localeGuardianName" disabled style="margin-right: 5px;"/>
                <a-button :disabled="!canChangeGuardian" @click="personSelectModalVisible = true">更换</a-button>
              </div>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="盲板规格-厚度(mm)">
              <a-input :value="formData.field7" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="盲板作业性质">
              <a-input :value="formData.workCategoryName" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="作业内容">
              <a-input :value="formData.workContent" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="盲板编号">
              <a-input :value="formData.field1" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="计划作业起止时间">
              <a-input :value="formData.planStartTime + '至' + formData.planEndTime" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="盲板具体位置">
              <a-input :value="formData.workAddress" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="实际作业起止时间">
              <a-input :value="actualTime" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="管线输送介质">
              <a-input :value="formData.field2" disabled/>
            </a-form-item>
          </a-col>
        </a-row>
      </a-form>
    </a-card>
    <person-history :history-datas="historyList" />
    <person-select-modal
      :visible.sync="personSelectModalVisible"
      :loading="confirmLoading"
      @ok="changeProctorHotPerson"
      title="选择人员"
      :dept-id="formData.applicantUnitId"
      :multiple="false"
    />
  </div>
</template>

<script>
import { mapGetters } from 'vuex'
import PersonHistory from '../component/PersonHistory'
import PersonSelectModal from '../component/PersonSelectModal'
export default {
  name: 'BasicInfo',
  components: { PersonHistory, PersonSelectModal },
  props: {
    ticketId: {
      type: String,
      default: '',
      required: true
    }
  },
  data () {
    return {
      formData: {},
      historyList: [],
      personSelectModalVisible: false,
      confirmLoading: false,
    }
  },
  computed: {
    ...mapGetters(['userInfo']),
    canChangeGuardian () {
      if (this.formData.status === 1) return this.userInfo.id === this.formData.applicantId // 草稿： 创建人可编辑
        // 提交审核后只有负责人和监护人可以更换人员
      if (this.formData.status >= 4) {
        return (this.userInfo.id === this.formData.applicantId || this.userInfo.id === this.formData.localeGuardianId)
      }
      return false
    },
    actualTime () {
      if (this.formData.actualStartTime && this.formData.actualEndTime) {
        return this.formData.actualStartTime + '至' + this.formData.actualEndTime
      }
      return ''
    }
  },
  methods: {
    loadDetailInfo () {
      this.getByIdRestful('/spang-safety/basic-information', this.ticketId).then(async ({ data, code }) => {
        if (code === 200) {
          let workTypeOptions = []
          const dictData = await this.getDict('WORK_TICKET_BLIND_PLATE_NATURE')
          if (dictData.success) {
            workTypeOptions = dictData.data
          }
          const workType = workTypeOptions.find(item => item.dictKey == data.workCategory)
          data.workCategoryName = workType ? workType.dictValue : ''
          this.code = data.code
          this.formData = data
        }
      })
    },
    getPersonList () {
      this.getById('/spang-safety/user-history', this.ticketId).then(({ data, code }) => {
        if (code === 200) {
          this.historyList = data
        }
      })
    },
    changeProctorHotPerson (users) {
      if (this.formData.localeGuardianId !== users[0].userId) {
        this.formData.localeGuardianId = users[0].userId
        this.formData.localeGuardianName = users[0].userName
        const params = {
          ...this.formData,
          id: this.ticketId,
          saveOrSubmit: 0
        }
        this.submitForm('/spang-safety/basic-information', params, 'put').then(({ data, code }) => {
            if (code === 200) {
              this.$notification['success']({
                message: '提示',
                description: '更换成功',
                duration: 8
              })
              this.personSelectModalVisible = false
            } else {
              this.$notification['error']({
                message: '提示',
                description: data.msg,
                duration: 8
              })
            }
          })
      }
    },
  },
  created () {
    this.loadDetailInfo()
    this.getPersonList()
  }
}
</script>

