<template>
  <a-card :bordered="false" style="margin: 15px;">
    <a-form-model :model="form" ref="ruleForm" :rules="rules" :label-col="{span: 7}" :wrapper-col="{span:10}">
      <a-row class="form-title">
        <h3 >{{ formTitle }}</h3>
      </a-row>
      <a-form-model-item label="作业证编号" v-if="id !== '-1'">
        <a-input
          v-model="form.code"
          disabled/>
      </a-form-model-item>
      <a-form-model-item label="申请单位">
        <a-input
          v-model="form.applicantUnitName"
          disabled/>
      </a-form-model-item>
      <a-form-model-item label="申请人">
        <a-input
          v-model="form.applicantName"
          disabled/>
      </a-form-model-item>
      <a-form-model-item label="单位类型" prop="constructionUnitType">
        <a-radio-group v-model="form.constructionUnitType">
          <a-radio :value="0">
            内部单位
          </a-radio>
          <a-radio :value="1">
            外来施工单位
          </a-radio>
        </a-radio-group>
      </a-form-model-item>
      <a-form-model-item label="施工单位" prop="field6">
        <a-input
          placeholder="请输入施工单位"
          v-model="form.field6"
        />
      </a-form-model-item>
      <a-form-model-item label="作业单位" prop="workUnitId">
        <department-select v-model="form.workUnitId" @change="workUnitChange" placeholder="请选择作业单位"></department-select>
      </a-form-model-item>
      <a-form-model-item label="班组" prop="workTeam">
        <a-input
          placeholder="请输入班组"
          v-model="form.workTeam"
        />
      </a-form-model-item>
      <a-form-model-item label="断路原因" prop="workContent">
        <a-input
          placeholder="请输入断路原因"
          v-model="form.workContent"
        />
      </a-form-model-item>
      <a-form-model-item label="作业位置" prop="workAddress">
        <a-input
          placeholder="请输入作业位置"
          v-model="form.workAddress"
        />
      </a-form-model-item>
      <a-form-model-item label="是否附图纸安全方案" prop="haveScheme">
        <a-radio-group v-model="form.haveScheme">
          <a-radio :value="0">
            是
          </a-radio>
          <a-radio :value="1">
            否
          </a-radio>
        </a-radio-group>
      </a-form-model-item>
      <a-form-model-item label="作业类型" prop="field1">
        <a-checkbox-group v-model="form.field1" :options="workType">
          <span slot="label" slot-scope="{ text }">{{ text }}</span>
        </a-checkbox-group>
      </a-form-model-item>
      <a-form-model-item label="断路作业示意图" help="格式:jpg png 大小:不超过5M">
        <a-upload
          :action="action"
          list-type="picture-card"
          :file-list="form.fileList"
          @preview="handlePreview"
          @change="handleChange"
        >
          <div v-if="form.fileList.length < 8">
            <a-icon type="camera" />
            <div class="ant-upload-text">
              添加图片
            </div>
          </div>
        </a-upload>
        <a-modal
          :visible="previewVisible"
          :footer="null"
          @cancel="previewVisible = false"
          :keyboard="false"
          :maskClosable="false"
        >
          <img alt="example" style="width: 100%" :src="previewImage" />
        </a-modal>
      </a-form-model-item>
      <a-form-model-item label="申请作业起止时间" prop="planStartEndTime">
        <a-range-picker
          @change="onPlanTimeChange"
          v-model="form.planStartEndTime"
          valueFormat="YYYY-MM-DD HH:mm:ss"
          showTime
          style="width: 100%"
          placeholder="请选择作业起止时间"
        />
      </a-form-model-item>
      <a-form-model-item label="现场监护人" prop="localeGuardianId">
        <person-list-select
          v-model="form.localeGuardianId"
          @change="userChanged"
          :deptId="form.applicantUnitId"
          placeholder="请选择现场监护人"
        />
      </a-form-model-item>
      <a-form-model-item label="断路作业负责人" prop="field2">
        <person-list-select
          v-model="form.field2"
          @change="leaderChanged"
          :deptId="form.applicantUnitId"
          placeholder="请选择项目负责人"
        />
      </a-form-model-item>
      <a-form-model-item label="作业人" prop="workTicketRelatedUserList">
        <div
          v-for="(people, index) in form.workTicketRelatedUserList"
          :key="people.userId">
          <div class="flex-input-button" :style="{marginBottom: index !== form.workTicketRelatedUserList.length - 1? '10px': ''}">
            <a-input
              :value="people.userName"
              style="margin-right: 10px;"
              disabled>
              <a-icon slot="addonAfter" type="delete" @click="deleteHotPerson(index, people)"/>
            </a-input>
            <a-button v-if="index === 0" @click="hotPersonSelectModalVisible = true">添加</a-button>
          </div>
        </div>
        <div class="flex-input-button" v-if="form.workTicketRelatedUserList && form.workTicketRelatedUserList.length === 0">
          <a-input
            style="margin-right: 10px;"
            placeholder="请选择作业人"
            disabled/>
          <a-button @click="hotPersonSelectModalVisible = true">添加</a-button>
        </div>
      </a-form-model-item>
      <a-divider />
      <a-row>
        <a-col :span="8">
          <a-form-model-item label="涉及其他特殊作业">
            <a-radio-group v-model="form.haveOtherAssignments">
              <a-radio :value="1">
                是
              </a-radio>
              <a-radio :value="0">
                否
              </a-radio>
            </a-radio-group>
          </a-form-model-item>
        </a-col>
        <a-col :span="1" :offset="13" v-if="form.haveOtherAssignments">
          <a-button icon="plus" @click="otherTaskModalVisible = true" >添加其他特殊作业</a-button>
        </a-col>
      </a-row>
      <a-row v-if="form.haveOtherAssignments">
        <a-table
          ref="table"
          size="default"
          :rowKey="record => record.id"
          :columns="columns"
          :dataSource="form.workTicketRelatedWorkList"
          :alert="false"
          :scroll="{ y: 350 }"
          :pagination="false"
        >
          <span slot="time" slot-scope="text, record">
            <template v-if="record.planStartTime && record.planEndTime">
              {{ record.planStartTime }}&nbsp;至&nbsp;{{ record.planEndTime }}
            </template>
          </span>
          <span slot="operation" slot-scope="text, record, index">
            <a @click="removeOtherTask(index)">X</a>
          </span>
        </a-table>
      </a-row>
      <div style="text-align: center; margin-top: 10px;">
        <a-button type="primary" @click="submitForm" style="width: 122px;">提交</a-button>
        <a-button type="second" @click="goBack" style="width: 122px;margin-left: 20px">返回</a-button>
      </div>
    </a-form-model>
    <other-task-modal
      :visible.sync="otherTaskModalVisible"
      :loading="otherTaskConfirm"
      @ok="addOtherTask"
    >
    </other-task-modal>
    <person-select-modal
      :visible.sync="hotPersonSelectModalVisible"
      title="添加作业人"
      @ok="changeHotPerson"
      :dept-id="form.applicantUnitId"
      :multiple="true"
    />
  </a-card>
</template>

<script>
import OtherTaskModal from '../component/OtherTaskModal'
import { otherTaskColumns, simpleTaskColumns } from '@/views/safety/table'
import { mapGetters } from 'vuex'
import { saveForm, getDetail } from '@/api/breaking-work'
import DepartmentSelect from '@/views/safety/components/DepartmentSelect'
import PersonListSelect from '../../components/PersonListSelect'
import PersonSelectModal from '../component/PersonSelectModal'
import { fileToBase64 } from '@/utils/util'
const workType = [{
    text: '挖土',
    value: '0'
  }, {
    text: '打桩',
    value: '1'
  }, {
    text: '地锚',
    value: '2'
  }, {
    text: '使用推土机填平',
    value: '3'
  }, {
    text: '使用压路机平整场地',
    value: '4'
  }, {
    text: '其它',
    value: '5'
  }]
export default {
  name: 'AddTikcet',
  components: { DepartmentSelect, OtherTaskModal, PersonListSelect, PersonSelectModal },
  props: {
    id: {
      type: String,
      required: true,
      default: '-1'
    }
  },
  data () {
    return {
      otherTaskModalVisible: false,
      otherTaskConfirm: false,
      hotPersonSelectModalVisible: false,
      workType,
      previewVisible: false,
      previewImage: '',
      form: {
        haveOtherAssignments: 0,
        workUnitName: '',
        localeGuardianName: '',
        field3: '',
        fileList: [],
        planStartTime: null,
        planEndTime: null,
        planStartEndTime: null,
        workTicketRelatedWorkList: [],
        haveScheme: 1,
        constructionUnitType: 0,
        field4:'',
        workTicketRelatedUserList: [],
      },
      rules: {
        localeGuardianId: [{ required: true, message: '请选择现场监护人', trigger: 'change' }],
        workUnitId: [{ required: true, message: '请选择作业单位', trigger: 'change' }],
        workAddress: [
          { required: true, message: '请输入作业位置', trigger: 'blur' }
        ],
        field1: [
          {
            type: 'array',
            required: true,
            message: '至少选择一种作业方式',
            trigger: 'change'
          }
        ],
        field2: [{ required: true, message: '请选择项目负责人', trigger: 'change' }],
        workContent: [
          { required: true, message: '请输入断路原因', trigger: 'blur' }
        ],
        // field6: [
        //   { required: true, message: '请输入施工单位', trigger: 'blur' }
        // ],
        workTeam: [
          { required: true, message: '请输入班组', trigger: 'blur' }
        ],
        planStartEndTime: [
          { required: true, message: '请选择动火起止时间', trigger: 'change' }
        ],
        workType: [{ required: true, message: '请选择作业类别', trigger: 'change' }],
    workTicketRelatedUserList: [
      {
        required: true,
        validator: (rule, value, callback) => {
          if (!value || value.length === 0) {
            callback(new Error('请选择作业人'))
          } else {
            callback()
          }
        },
        trigger: 'change'
      }
    ]
      },
      action: this.BASE_URL + '/spang-safety/fileserver/filesUpload',
    }
  },
  computed: {
    ...mapGetters(['userInfo']),
    formTitle () {
      if (this.id === '-1') {
        return '新建断路作业票'
      } else {
        return '修改断路作业票'
      }
    },
    columns () {
      return [...simpleTaskColumns, {
        title: '操作',
        dataIndex: 'operation',
        align: 'center',
        width: '100px',
        scopedSlots: { customRender: 'operation' }
      }]
    }
  },
  methods: {
    onPlanTimeChange (date) {
      this.form.planStartTime = date[0]
      this.form.planEndTime = date[1]
      this.form.planStartEndTime = date
    },
    filterOption (input, option) {
      return (
        option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
      )
    },
    goBack () {
      this.$router.push('/safety/task/manage')
    },
    changeHotPerson (users) {
      let userInfo = [];
      users.forEach(item => {
        userInfo.push({userId: item.userId,userName: item.userName,code: item.certificateCode,isSign : 0})
      })
      const personList = [...this.form.workTicketRelatedUserList, ...userInfo]
      const obj = {}
      this.form.workTicketRelatedUserList = personList.reduce((cur, next) => {
        obj[next.userId] ? '' : obj[next.userId] = true && cur.push(next)
        return cur
      }, [])
      this.hotPersonSelectModalVisible = false;
    },
    deleteHotPerson (index) {
      this.form.workTicketRelatedUserList.splice(index, 1)
    },
    submitForm () {
      this.$refs.ruleForm.validate(valid => {
        if (valid) {
          if (this.form.constructionUnitType === 1 && !this.form.field6) {
            this.$message.warning('请输入施工单位')
            return
          }
          const params = Object.assign({}, this.form)
          delete params.planStartEndTime
          params.status = 2
          if (this.form.haveOtherAssignments) {
            params.workTicketRelatedWorkList = this.form.workTicketRelatedWorkList.map(item => ({
              code: item.code,
              relatedTicketId: item.relatedTicketId,
              workType: item.workType,
              workTypeName: item.workTypeName,
            }))
          }
          params.field1 = params.field1.toString();
          params.ticketType = '7';
          let method = 'post'
          if (this.id !== '-1') {
            method = 'put'
          }
          const url = `/spang-safety/basic-information`;
          this.submitFormApi(method,url, params).then(res=>{
            if(res.code === 200){
              this.$notification['success']({
                message: '提示',
                description: '提交成功',
                duration: 8
              })
              this.$router.push('/safety/task/manage');
            }
          })
        } else {
          console.error('error submit!!')
          return false
        }
      })
    },
    loadFormData () {
      this.getList('/spang-safety/basic-information/' + this.id,{}).then(res => {
        if (res.code === 200) {
          this.form = Object.assign({ planStartEndTime: [res.data.planStartTime, res.data.planEndTime],workTicketRelatedWorkList:[], workTicketRelatedUserList: [],fileList: [] }, res.data)
          if(this.form.field4 !== ''){
            this.form.field4 = this.form.field4.split(',');
            this.form.field4.forEach((f,i)=>{
              this.form.fileList.push({uid: i + 1,url: this.BASE_URL + f,status:'done'})
            })
            this.form.field4 = this.form.field4.toString();
          }
        }
      })
    },
    workUnitChange ({ id, label }) {
      this.form.workUnitName = label
    },
    userChanged ({ id, person }) {
      this.form.localeGuardianName = person.realName
    },
    leaderChanged ({ id, person }) {
      this.form.field3 = person.realName
    },
    removeOtherTask (index) {
      this.form.workTicketRelatedWorkList.splice(index, 1)
    },
    addOtherTask (data) {
      data.forEach(d=>{
        d.workTypeName = d.ticketTypeName;
        d.relatedTicketId = d.id;
        d.workTypeName = d.ticketTypeName;
        d.workType = d.ticketType;
      })
      const ids = this.form.workTicketRelatedWorkList.map(item => item.relatedTicketId)
      const newTasks = data.filter(item => !ids.includes(item.relatedTicketId))
      this.form.workTicketRelatedWorkList = [...this.form.workTicketRelatedWorkList, ...newTasks]
      this.otherTaskModalVisible = false
    },
    async handlePreview (file) {
      if (!file.url && !file.preview) {
        file.preview = await fileToBase64(file.originFileObj)
      }
      this.previewImage = file.url || file.preview
      this.previewVisible = true
    },
    handleChange ({ fileList }) {
      this.form.fileList = fileList;
      this.form.field4 = '';
      fileList.forEach((f,i)=>{
        let n = fileList.length - 1;
        if(n !== i){
          if(!this.form.field4 || this.form.field4 === ''){
            this.form.field4 = f.url.slice(4);
          }else{
            this.form.field4 += ',' + f.url.slice(4);
          }
        }else{
          if(!this.form.field4 || this.form.field4 === ''){
            if(f.response){
              this.form.field4 = '/spang-safety/file' + f.response.data[0].urlPath;
            }else{
              this.form.field4 = '/spang-safety/file' + f.url;
            }
          }else{
            if(f.response){
              this.form.field4 += ',' + '/spang-safety/file' + f.response.data[0].urlPath;
            }else{
              this.form.field4 += ',' + '/spang-safety/file' + f.url;
            }
          }
        }
        this.form.fileList = [...fileList]
      })
    }

  },
  created () {
    if (this.id !== '-1') {
      this.loadFormData()
    } else {
      this.form.applicantUnitName = this.userInfo.deptName
      this.form.applicantUnitId = this.userInfo.deptId
      this.form.applicantName = this.userInfo.realName
      this.form.applicantId = this.userInfo.id
    }
  }
}
</script>

<style scoped lang="less">
  .flex-input-button {
    display: flex;
    justify-content: space-between;
  }
  .helper-text {
    color: rgba(0, 0, 0, 0.25);
  }
</style>
