<template>
  <div>
    <a-card
      :bordered="false"
      style="margin: 15px;"
      :headStyle="{border: 'none', paddingBottom: 0}"
      :bodyStyle="{paddingTop: 0, paddingBottom: 0}"
      class="table-card">
      <template #title>
        <div>
          <div class="title-lg">
            {{ title }}
          </div>
          <div class="title-sm">
            <span>断路作业</span><span style="margin-left: 10px; font-weight: normal">{{ code }}</span>
          </div>
        </div>
        <div class="card-action">
          <a-button type="second" @click="goBack" style="width: 122px;">返回</a-button>
        </div>
      </template>
      <a-tabs :default-active-key="0" @change="handleTabChange">
        <a-tab-pane
          :key="index"
          :tab="item.name"
          v-for="(item, index) in tabs"
          :disabled="!item.statusList.includes(ticketStatus)">
        </a-tab-pane>
      </a-tabs>
    </a-card>

    <keep-alive>
      <component :is="currentTabComponent" :ticket-id="ticketId"></component>
    </keep-alive>

  </div>

</template>

<script>
import BasicInfo from './BasicInfo'
import Confirmation from './Confirmation'
import Approval from './Approval'
import Inspection from './Inspection'
export default {
  name: 'BreakingWorkDetail',
  components: { BasicInfo, Confirmation, Approval, Inspection },
  props: {
    ticketId: {
      type: String,
      required: true
    }
  },
  data () {
    return {
      code: '',
      ticketStatus: 1,
      title: '作业详情',
      tabs: [
        {
          name: '作业详情',
          component: 'BasicInfo',
          statusList: [0, 1, 2, 3, 4, 5, 6]
        },
        {
          name: '现场确认',
          component: 'Confirmation',
          statusList: [3, 4, 5, 6, 0]
        },
        {
          name: '作业审核',
          component: 'Approval',
          statusList: [4, 5, 6, 0]
        },
        {
          name: '作业验收',
          component: 'Inspection',
          statusList: [6, 0]
        }
      ],
      currentTabComponent: 'BasicInfo'
    }
  },
  methods: {
    handleTabChange (activeKey) {
      this.currentTabComponent = this.tabs[activeKey].component
      this.title = this.tabs[activeKey].name
    },
    loadDetailInfo () {
      this.getByIdRestful('/spang-safety/basic-information', this.ticketId).then(({ data, code }) => {
        if (code === 200) {
          this.code = data.code
          this.ticketStatus = data.status
        }
      })
    },
    goBack () {
      this.$router.push('/safety/task/manage')
    }
  },
  created () {
    if (this.ticketId !== '-1') {
      this.loadDetailInfo()
    }
  }
}
</script>

