<template>
  <a-list>
    <a-list-item>
      <div class="list-content">
        <div class="list-content-item">
          <span>{{ listData.title1 }}</span>
          <p>{{ listData.person1 || ' ' }}</p>
        </div>
        <div class="list-content-item" style="text-align: center">
          <sign-img :src=" listData.sign1 ? BASE_URL + '/spang-safety/file' + listData.sign1: ''"></sign-img>
        </div>
        <div class="list-content-item">
          <span>签字时间</span>
          <p>{{ listData.time1 || ' ' }}</p>
        </div>
        <div class="list-content-item" style="text-align: right" v-if="listData.files1 && listData.files1.length > 0">
          <a @click="showImage(listData.files1)">查看图片</a>
        </div>
        <div class="list-content-item" v-else>

        </div>
      </div>
    </a-list-item>
    <a-list-item v-if='listData.sign2'>
      <div class="list-content">
        <div class="list-content-item">
          <span>{{ listData.title2 }}</span>
          <p>{{ listData.person2 || ' ' }}</p>
        </div>
        <div class="list-content-item" style="text-align: center">
          <sign-img :src="listData.sign2 ? BASE_URL + '/spang-safety/file' + listData.sign2 : ''"></sign-img>
        </div>
        <div class="list-content-item">
          <span>签字时间</span>
          <p>{{ listData.time2 || ' ' }}</p>
        </div>
        <div class="list-content-item" style="text-align: right" v-if="listData.files2 && listData.files2.length > 0">
          <a @click="showImage(listData.files2)">查看图片</a>
        </div>
        <div class="list-content-item" v-else>

        </div>
      </div>
    </a-list-item>
  </a-list>
</template>

<script>
import SignImg from './SignImg'
export default {
  name: 'HorizontalList',
  components: { SignImg },
  props: {
    listData: {
      type: Object,
      default: () => {},
      required: true
    }
  },
  methods: {
    showImage (files) {
      if (files.length === 0) {
        this.$message.info('暂无图片')
        return
      }
      const images = files.map(item => this.BASE_URL + '/spang-safety/file' + item.filePath)
      this.$viewerApi({
        options: {
          navbar: false,
          fullscreen: false,
          inline: true
        },
        images: images
      })
    }
  }
}
</script>

