<template>
  <a-list>
    <a-list-item v-for="(item, index) in listData" :key="index">
      <div class="list-content">
        <div class="list-content-item">
          <span>{{ item.title }}</span>
          <p>{{ item.person || ' ' }}</p>
        </div>
        <div class="list-content-item">
          <sign-img :src="item.sign ? BASE_URL + '/spang-safety/file' + item.sign : ''"></sign-img>
        </div>
        <div class="list-content-item">
          <span>签字时间</span>
          <p>{{ item.time || ' ' }}</p>
        </div>
        <div class="list-content-item" style="text-align: right">
          <a v-if="isShowImage" @click="showImage(item.files)">查看图片</a>
        </div>
      </div>
    </a-list-item>
  </a-list>
</template>

<script>
import SignImg from './SignImg'
export default {
  name: 'SignatureList',
  components: { SignImg },
  props: {
    listData: {
      type: Array,
      default: () => [],
      required: true
    },
    isShowImage: {
      type: Boolean,
      default: false
    }
  },
  methods: {
    showImage (files) {
      if (files.length === 0) {
        this.$message.info('暂无图片')
        return
      }
      const images = files.map(item => this.BASE_URL + '/spang-safety/file' + item.filePath)
      this.$viewerApi({
        options: {
          navbar: false,
          fullscreen: false,
          inline: true
        },
        images: images
      })
    }
  }
}
</script>

