<template>
  <a-card style="margin: 15px;">
    <a-tabs
      default-active-key="1"
      tab-position="left"
      @change="handleTabChange"
    >
      <a-tab-pane key="1" tab="风险辨识">
        <a-list
          class="demo-loadmore-list"
          item-layout="horizontal"
          :data-source="riskDatas"
        >
          <a-list-item slot="renderItem" slot-scope="item, index">
            <a slot="actions" v-if="false">查看图片</a>
            <a-list-item-meta>
              <div slot="description">
                <span
                  style="margin-right: 5px;"
                  :class="item.isTitle === 1 ? 'color-red' : 'color-green'">{{ item.isTitle === 1 ? '有' : '不涉及' }}</span>
                <template v-if="item.measureLabel !== 'false'">
                  <span>安全措施: </span>
                  <template v-if="!item.options">
                    <span>{{ item.measureLabel }}</span>
                  </template>
                  <template v-else>
                    <div class="measure-content-desc">
                      <template v-for="(option, index) in item.options">
                        <div class="measure-content-item" :key="index">
                          <a-checkbox :checked="item.measuresValue.includes(option.value)"></a-checkbox>
                          <label-with-input :label="option.text" :label-values="option.textValues"/>
                        </div>
                      </template>
                    </div>
                  </template>
                </template>
                <template v-else>
                  <span>{{ item.content }}</span>
                </template>
              </div>
              <div slot="title" class="font-weight-bold">
                {{ index + 1 }}.
                <label-with-input :label="item.label" :label-values="item.titleValue.split(',')"/>
              </div>
            </a-list-item-meta>

          </a-list-item>
        </a-list>
        <horizontal-list :list-data="safetyFormSign"></horizontal-list>
      </a-tab-pane>
      <a-tab-pane key="2" tab="安全签字">
        <div class="measure-title">监护人</div>
        <signature-list :list-data="protoChargerSign"></signature-list>
        <a-divider class="measure-title-divider"/>
        <div class="measure-title">作业负责人</div>
        <signature-list :list-data="workChargerSign"></signature-list>
        <a-divider class="measure-title-divider"/>
        <div class="measure-title">作业人员签字</div>
        <signature-list :list-data="workersSign"></signature-list>
        <a-divider class="measure-title-divider"/>
        <div class="measure-title">工艺：技术员以上人员</div>
        <signature-list :list-data="otherSign"></signature-list>
        <a-divider class="measure-title-divider"/>
        <div class="measure-title">设备：技术员以上人员</div>
        <signature-list :list-data="otherSign1"></signature-list>
        <a-divider class="measure-title-divider"/>
        <div class="measure-title">班长或安全员</div>
        <signature-list :list-data="otherSign2"></signature-list>
        <a-divider class="measure-title-divider"/>
        <horizontal-list :list-data="safetyFormSign"></horizontal-list>
        <template v-if="presentImages.length > 0">
          <a-divider/>
          <div class="measure-title">
            现场图片
            <viewer :images="presentImages" class="present-img-viewer">
              <img v-for="src in presentImages" :key="src" :src="src" class="present-img">
            </viewer>
          </div>
        </template>
      </a-tab-pane>
      <a-tab-pane key="3" tab="安全交底">
        <safety-disclosure :content-data="safetyForm"></safety-disclosure>
        <horizontal-list :list-data="safetyFormSign"></horizontal-list>
      </a-tab-pane>
    </a-tabs>
  </a-card>
</template>

<script>
import { hotWorkRiskFillinList } from './risks'
import { safetyForm } from '../component/risk'
import LabelWithInput from '../../ticket/component/LabelWithInput'
import { mapGetters } from 'vuex'
import SignImg from '../component/SignImg'
import HorizontalList from '../component/HorizontalList'
import SafetyDisclosure from '../component/SafetyDisclosure'
import SignatureList from '../component/SignatureList'
import cloneDeep from 'lodash.clonedeep'
export default {
  name: 'Confirmation',
  components: { LabelWithInput, SignImg, HorizontalList, SafetyDisclosure, SignatureList },
  props: {
    ticketId: {
      type: String,
      default: '',
      required: true
    }
  },
  data () {
    return {
      riskDatas: hotWorkRiskFillinList,
      safetyForm: cloneDeep(safetyForm),
      safetyFormSign: {
        title1: '申请单位',
        title2: '作业单位'
      },
      protoChargerSign: [],
      workChargerSign: [],
      workersSign: [],
      otherSign: [],
      otherSign1: [],
      otherSign2: [],
      presentImages: [],
      options: {
        navbar: false,
        fullscreen: false
      }
    }
  },
  computed: {
     ...mapGetters(['userInfo'])
  },
  methods: {
    handleTabChange (tabIndex) {
      if (tabIndex === '1') {
        this.safetyFormSign = {
          title1: '申请单位',
          title2: '作业单位'
        }
        this.loadRiskDatas()
      } else if (tabIndex === '3') {
        this.safetyFormSign = {
          title1: '作业单位安全教育人',
          title2: '所在单位安全教育人'
        }
        this.loadSafefillin()
      } else if (tabIndex === '2') {
        this.safetyFormSign = {
          title1: '作业单位安全教育人',
          title2: '所在单位安全教育人'
        }
        this.loadSafetySign()
      }
    },
    loadSafetySign () {
      this.getById('/spang-safety/locale-confirm/sign', this.ticketId).then(({ data, code }) => {
        if (code === 200) {
          this.workChargerSign = data['1'].map(item => ({
            person: item.userName,
            time: item.signTime,
            sign: item.signatureUrl
          }))
          this.protoChargerSign = data['2'].map(item => ({
            person: item.userName,
            time: item.signTime,
            sign: item.signatureUrl
          }))
          this.workersSign = data['0'].map(item => ({
            person: item.userName,
            time: item.signTime,
            sign: item.signatureUrl
          }))
          this.otherSign = data['5'].map(item => ({
            time: item.signTime,
            sign: item.signatureUrl
          }))
          this.otherSign1 = data['6'].map(item => ({
            time: item.signTime,
            sign: item.signatureUrl
          }))
          this.otherSign2 = data['7'].map(item => ({
            time: item.signTime,
            sign: item.signatureUrl
          }))
        }
      })
      const params = {
        id: this.ticketId,
        type: 2
      }
      this.getList('/spang-safety/locale-confirm', params).then(({ data, code }) => {
        if (code === 200) {
          this.presentImages = data.files.map(item => (this.BASE_URL + '/spang-safety/file' + item.filePath))
          data.signUsers.forEach(item => {
              if (item.userType === 0) {
                this.safetyFormSign = Object.assign({}, this.safetyFormSign, {
                  title1: '作业单位安全教育人',
                  time1: item.signDate,
                  sign1: item.signatureURL
                })
              }
              if (item.userType === 1) {
                this.safetyFormSign = Object.assign({}, this.safetyFormSign, {
                  title2: '所在单位安全教育人',
                  time2: item.signDate,
                  sign2: item.signatureURL
                })
              }
					})
        }
      })
    },
    loadRiskDatas () {
      const params = {
        id: this.ticketId,
        type: 0
      }
      this.getList('/spang-safety/locale-confirm', params).then(({ data, code }) => {
        if (code === 200) {
					const content = data.content ? JSON.parse(data.content) : {}
					for (const key in this.riskDatas) {
						Object.assign(this.riskDatas[key], content[key])
					}
          data.signUsers.forEach(item => {
              if (item.userType === 0) {
                this.safetyFormSign = Object.assign({}, this.safetyFormSign, {
                  title1: '申请单位',
                  time1: item.signDate,
                  sign1: item.signatureURL
                })
              }
              if (item.userType === 1) {
                this.safetyFormSign = Object.assign({}, this.safetyFormSign, {
                  title2: '作业单位',
                  time2: item.signDate,
                  sign2: item.signatureURL
                })
              }
					})
        }
      })
    },
    loadSafefillin () {
      const params = {
        id: this.ticketId,
        type: 4
      }
      this.getList('/spang-safety/locale-confirm', params).then(({ data, code }) => {
        if (code === 200) {
          const content = data.content ? JSON.parse(data.content) : {}
          const contentData = { riskType: [], measures: [] }
          for (const key in contentData) {
          	Object.assign(contentData[key], content[key])
          }
          contentData.riskType.forEach(item => {
            Object.assign(item, { fillIn: item.factor })
          })

          contentData.measures.forEach(item => {
            Object.assign(item, { fillIn: item.factor })
          })
          this.safetyForm = contentData
          data.signUsers.forEach(item => {
              if (item.userType === 0) {
                this.safetyFormSign = Object.assign({}, this.safetyFormSign, {
                  title1: '作业单位安全教育人',
                  time1: item.signDate,
                  sign1: item.signatureURL
                })
              }
              if (item.userType === 1) {
                this.safetyFormSign = Object.assign({}, this.safetyFormSign, {
                  title2: '所在单位安全教育人',
                  time2: item.signDate,
                  sign2: item.signatureURL
                })
              }
					})
        }
      })
    }
  },
  created () {
    this.loadRiskDatas()
  }
}
</script>

<style scoped lang="less">
  .color-red {
    color: #F26161;
  }

  .color-green {
    color: #10B24C;
  }
  .font-weight-bold {
    font-size: 14px;
    font-weight: 600;
  }
  .flex-row {
    display: flex;
    .el-col {
      float: none;
    }
  }

  .measure-content-desc {
    display: inline-flex;
  }
  .measure-content-item {
    display: inline-block;
    margin-right: 10px;
    line-height: 1.4;
  }
  .present-img-viewer {
    text-align: center;
  }
  .present-img {
    width: 50px;
    height: 50px;
    margin: 10px;
    cursor: pointer;
  }
  .measure-title-divider {
    margin: 5px 0;
  }
</style>
