<template>
  <div>
    <a-card title="基本信息" style="margin: 15px;">
      <a-form class="ant-advanced-search-form">
        <a-row :gutter="24">
          <a-col :span="10" :offset="1">
            <a-form-item label="作业证编号">
              <a-input :value="formData.code" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="作业地点">
              <a-input :value="formData.workAddress" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="作业类型">
              <a-input :value="formData.constructionUnitType ? '外来施工单位' : '厂内维修'" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="电源接入点">
              <a-input :value="formData.workContent" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="申请人">
              <a-input :value="formData.applicantName" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="工作电压">
              <a-input :value="formData.field1" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="申请单位">
              <a-input :value="formData.applicantUnitName" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="用电设备及功率">
              <a-input :value="formData.field2" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="联系电话">
              <a-input :value="formData.field3" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="计划作业起止时间">
              <a-input :value="formData.planStartTime + '至' + formData.planEndTime" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="作业单位">
              <a-input :value="formData.workUnitName" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="实际作业起止时间">
              <a-input :value="actualTime" disabled/>
            </a-form-item>
          </a-col>
        </a-row>
      </a-form>
      <a-divider style="margin: 0;"/>
      <a-row :gutter="24">
        <a-col :span="24">
          <div style="margin: 10px 0;">接线电工:</div>
        </a-col>
        <a-table
          ref="table"
          size="default"
          :rowKey="record => record.id"
          :columns="columns"
          :dataSource="formData.workTicketRelatedUserList"
          :alert="false"
          :scroll="{ y: 350 }"
          :pagination="false"
        >
        </a-table>
      </a-row>
    </a-card>
    <person-history :history-datas="historyList" />
  </div>
</template>

<script>
import PersonHistory from '../component/PersonHistory'
import { mapGetters } from 'vuex'
export default {
  name: 'BasicInfo',
  components: { PersonHistory },
  props: {
    ticketId: {
      type: String,
      default: '',
      required: true
    }
  },
  data () {
    return {
      ticketStatus: 1,
      formData: {
        workTicketRelatedUserList: []
      },
      historyList: [],
      columns: [
        {
          title: '姓名',
          dataIndex: 'userName',
          align: 'center',
          width: '150px'
        },
        {
          title: '作业编号',
          dataIndex: 'code',
          align: 'left'
        }
      ]
    }
  },
  computed: {
    ...mapGetters(['userInfo']),
    actualTime () {
      if (this.formData.actualStartTime && this.formData.actualEndTime) {
        return this.formData.actualStartTime + '至' + this.formData.actualEndTime
      }
      return ''
    }
  },
  methods: {
    loadDetailInfo () {
      this.getByIdRestful('/spang-safety/basic-information', this.ticketId).then(({ data, code }) => {
        if (code === 200) {
          this.code = data.code
          this.formData = data
          this.ticketStatus = data.status
        }
      })
    },
    getPersonList () {
      this.getById('/spang-safety/user-history', this.ticketId).then(({ data, code }) => {
        if (code === 200) {
          this.historyList = data
        }
      })
    }
  },
  created () {
    this.loadDetailInfo()
    this.getPersonList()
  }
}
</script>

