<template>
  <a-card style="margin: 15px;">
    <a-tabs
      default-active-key="1"
      tab-position="left"
      @change="handleTabChange"
    >
      <a-tab-pane key="1" tab="风险辨识">
        <a-list
          class="demo-loadmore-list"
          item-layout="horizontal"
          :data-source="riskDatas"
        >
          <a-list-item slot="renderItem" slot-scope="item, index">
            <a slot="actions" v-if="false">查看图片</a>
            <a-list-item-meta>
              <div slot="description">
                <span
                  style="margin-right: 5px;"
                  :class="item.value === 1 ? 'color-red' : 'color-green'">{{ item.value === 1 ? '有' : '不涉及' }}</span>
                <template v-if="item.measureLabel !== 'false'">
                  <span>{{ item.measureLabel }}: </span>
                  <span>{{ item.code }}</span>
                </template>
                <template v-if="index === riskDatas.length - 1" >
                  <span>{{ item.content }}</span>
                </template>
              </div>
              <div slot="title" class="font-weight-bold">
                {{ index + 1 }}.
                <label-with-input :label="item.label" :label-values="item.titleValue"/>
              </div>
            </a-list-item-meta>

          </a-list-item>
        </a-list>
        <signature-list :list-data="[safetyFormSign]"></signature-list>
      </a-tab-pane>
      <a-tab-pane key="2" tab="安全签字">
        <div class="measure-title">申请人</div>
        <signature-list :list-data="protoChargerSign"></signature-list>
        <a-divider class="measure-title-divider"/>
<!--        <div class="measure-title">作业负责人</div>-->
<!--        <signature-list :list-data="workChargerSign"></signature-list>-->
<!--        <a-divider class="measure-title-divider"/>-->
        <div class="measure-title">作业人员签字</div>
        <signature-list :list-data="workersSign"></signature-list>
        <a-divider class="measure-title-divider"/>
      </a-tab-pane>
      <a-tab-pane key="3" tab="安全交底">
        <safety-disclosure :content-data="safetyForm"></safety-disclosure>
        <signature-list :list-data="[safetyFormSign]"></signature-list>
      </a-tab-pane>
    </a-tabs>
  </a-card>
</template>

<script>
import { hotWorkRiskFillinList } from './risks'
import { safetyForm } from '../component/risk'
import LabelWithInput from '../../ticket/component/LabelWithInput'
import { mapGetters } from 'vuex'
import SignImg from '../component/SignImg'
import HorizontalList from '../component/HorizontalList'
import SafetyDisclosure from '../component/SafetyDisclosure'
import SignatureList from '../component/SignatureList'
import cloneDeep from 'lodash.clonedeep'
export default {
  name: 'Confirmation',
  components: { LabelWithInput, SignImg, HorizontalList, SafetyDisclosure, SignatureList },
  props: {
    ticketId: {
      type: String,
      default: '',
      required: true
    }
  },
  data () {
    return {
      riskDatas: hotWorkRiskFillinList,
      safetyForm: cloneDeep(safetyForm),
      safetyFormSign: {
        title: '申请人'
      },
      protoChargerSign: [],
      workChargerSign: [],
      workersSign: []
    }
  },
  computed: {
     ...mapGetters(['userInfo'])
  },
  methods: {
    handleTabChange (tabIndex) {
      if (tabIndex === '1') {
        this.loadRiskDatas()
      } else if (tabIndex === '2') {
        this.loadSafetySign()
      } else {
        this.loadSafefillin()
      }
    },
    loadSafetySign () {
      this.getById('/spang-safety/locale-confirm/sign', this.ticketId).then(({ data, code }) => {
        if (code === 200) {
          // this.workChargerSign = data['1'].map(item => ({
          //   person: item.userName,
          //   time: item.signTime,
          //   sign: item.signatureUrl ? this.BASE_URL + '/spang-safety/file' + item.signatureUrl : ''
          // }))
          this.protoChargerSign = data['4'].map(item => ({
            person: item.userName,
            time: item.signTime,
            sign: item.signatureUrl
          }))
          this.workersSign = data['0'].map(item => ({
            person: item.userName,
            time: item.signTime,
            sign: item.signatureUrl
          }))
        }
      })
    },
    loadRiskDatas () {
      const params = {
        id: this.ticketId,
        type: 0
      }
      this.getList('/spang-safety/locale-confirm', params).then(({ data, code }) => {
        if (code === 200) {
					const content = data.content ? JSON.parse(data.content) : {}
					for (const key in this.riskDatas) {
						Object.assign(this.riskDatas[key], content[key])
					}
          this.safetyFormSign = Object.assign({}, this.safetyFormSign, {
            time: data.signUsers[0].signDate,
            sign: data.signUsers[0].signatureURL
          })
        }
      })
    },
    loadSafefillin () {
      const params = {
        id: this.ticketId,
        type: 4
      }
      this.getList('/spang-safety/locale-confirm', params).then(({ data, code }) => {
        if (code === 200) {
          const content = data.content ? JSON.parse(data.content) : {}
          const contentData = { riskType: [], measures: [] }
          for (const key in contentData) {
          	Object.assign(contentData[key], content[key])
          }
          contentData.riskType.forEach(item => {
            Object.assign(item, { fillIn: item.factor })
          })

          contentData.measures.forEach(item => {
            Object.assign(item, { fillIn: item.factor })
          })
          this.safetyForm = contentData
          this.safetyFormSign = Object.assign({}, this.safetyFormSign, {
            time: data.signUsers[0].signDate,
            sign: data.signUsers[0].signatureURL ? this.BASE_URL + '/spang-safety/file' + data.signUsers[0].signatureURL : ''
          })
        }
      })
    }
  },
  created () {
    this.loadRiskDatas()
  }
}
</script>

