    display: flex;
    .el-col {
      float: none;
    }
  }
  .measure-title-divider {
    margin: 5px 0;
  }
  .measure-content-desc {
    display: inline-flex;
  }
  .measure-content-item {
    display: inline-block;
    margin-right: 10px;
    line-height: 1.4;
  }

</style>
