<template>
  <div>
    <a-card title="基本信息" style="margin: 15px;">
      <a-form class="ant-advanced-search-form">
        <a-row :gutter="24">
          <a-col :span="10" :offset="1">
            <a-form-item label="作业证编号">
              <a-input :value="formData.code" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="动火高度(米)">
              <a-input :value="formData.field1" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="申请单位">
              <a-input :value="formData.applicantUnitName" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="作业类别">
              <a-input :value="formData.workTypeName" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="申请人">
              <a-input :value="formData.applicantName" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="作业分级">
              <a-input :value="formData.workLevelName" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="现场监护人">
              <div class="flex-input-button">
                <a-input :value="formData.localeGuardianName" disabled style="margin-right: 5px;"/>
                <a-button :disabled="!canChangeGuardian" @click="personSelectModalVisible = true">更换</a-button>
              </div>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="是否附安全方案">
              <a-input :value="formData.haveScheme === 0 ? '否': '是'" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="作业单位">
              <a-input :value="formData.workUnitName" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="计划作业起止时间">
              <a-input :value="formData.planStartTime + '至' + formData.planEndTime" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="作业内容">
              <a-input :value="formData.workContent" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="实际作业起止时间">
              <a-input :value="actualTime" disabled/>
            </a-form-item>
          </a-col>
          <a-col :span="10" :offset="1">
            <a-form-item label="作业地点">
              <a-input :value="formData.workAddress" disabled/>
            </a-form-item>
          </a-col>
        </a-row>
      </a-form>
      <a-divider style="margin: 0;"/>
      <a-row :gutter="24">
        <a-col :span="24">
          <div style="margin: 10px 0;">涉及其他特殊作业:</div>
        </a-col>
        <a-table
          ref="table"
          size="default"
          :rowKey="record => record.id"
          :columns="otherTaskColumns"
          :dataSource="formData.workTicketRelatedWorkList"
          :alert="false"
          :scroll="{ y: 350 }"
          :pagination="false"
        >
          <span slot="time" slot-scope="text, record">
            <template v-if="record.planStartTime && record.planEndTime">
              {{ record.planStartTime }}&nbsp;至&nbsp;{{ record.planEndTime }}
            </template>
          </span>
        </a-table>
      </a-row>
    </a-card>
    <person-history :history-datas="historyList" />
    <person-select-modal
      :visible.sync="personSelectModalVisible"
      :loading="confirmLoading"
      @ok="changeProctorHotPerson"
      title="添加人员"
      :dept-id="userInfo.applicantUnitId"
      :multiple="false"
    />
  </div>
</template>

<script>
import { otherTaskColumns } from '@/views/safety/table'
import { getUserAuth, getDict } from '@/api/system'
import PersonSelectModal from '../component/PersonSelectModal'
import { mapGetters } from 'vuex'
import PersonHistory from '../component/PersonHistory'
export default {
  name: 'BasicInfo',
  components: { PersonHistory, PersonSelectModal },
  props: {
    ticketId: {
      type: String,
      default: '',
      required: true
    }
  },
  data () {
    return {
      ticketStatus: 1,
      formData: {
        workTicketRelatedWorkList: []
      },
      otherTaskColumns: otherTaskColumns,
      personSelectModalVisible: false,
      confirmLoading: false,
      workTypes: [],
      levelOptions: [],
      historyList: []
    }
  },
  computed: {
    ...mapGetters(['userInfo']),
			canChangeGuardian () {
				if (this.ticketStatus === 0) return false
				else if (this.ticketStatus >= 4) return (this.userInfo.id === this.formData.applicantId || this.userInfo.id === this.formData.applicantId)
				else return false
			},
    actualTime () {
      if (this.formData.actualStartTime && this.formData.actualEndTime) {
        return this.formData.actualStartTime + '至' + this.formData.actualEndTime
      }
      return ''
    }
  },
  methods: {
    loadDetailInfo () {
      this.getByIdRestful('/spang-safety/basic-information', this.ticketId).then(({ data, code }) => {
        if (code === 200) {
          this.code = data.code
          const workTypeObj = this.workTypes.find(item => item.dictKey == data.workCategory)
          data.workTypeName = workTypeObj ? workTypeObj.dictValue : ''
          const workLevelObj = this.levelOptions.find(item => item.dictKey == data.workLevel)
          data.workLevelName = workLevelObj ? workLevelObj.dictValue : ''
          this.formData = data
          this.ticketStatus = data.status
        }
      })
    },
    changeProctorHotPerson (users) {
      if (this.formData.localeGuardianId !== users[0].userId) {
        this.formData.localeGuardianId = users[0].userId
        this.formData.localeGuardianName = users[0].userName
        const params = {
          id: this.ticketId,
          localeGuardianId: this.formData.localeGuardianId,
          localeGuardianName: this.formData.localeGuardianName
        }
        this.submitForm('/spang-safety/basic-information', params, 'put').then(({ data, code }) => {
            if (code === 200) {
              this.$notification['success']({
                message: '提示',
                description: '更换成功',
                duration: 8
              })
              this.personSelectModalVisible = false
            } else {
              this.$notification['error']({
                message: '提示',
                description: data.msg,
                duration: 8
              })
            }
          })
      }
    },
    getDicts () {
      getDict('HIGH_WORK_TYPE').then(({ data }) => {
				this.workTypes = data
			})
      getDict('HIGH_WORK_LEVEL').then(({ data }) => {
				this.levelOptions = data
			})
    },
    getPersonList () {
      this.getById('/spang-safety/user-history', this.ticketId).then(({ data, code }) => {
        if (code === 200) {
          this.historyList = data
        }
      })
    }
  },
  async created () {
    await this.getDicts()
    this.loadDetailInfo()
    this.getPersonList()
  }
}
</script>

<style scoped lang="less">

/deep/ .ant-advanced-search-form .ant-form-item {
  display: flex;
}
/deep/ .ant-advanced-search-form .ant-form-item-label {
  width: 130px;
}

/deep/ .ant-advanced-search-form .ant-form-item-control-wrapper {
  flex: 1;
}
/deep/ .ant-table {
  height: auto;
}
.flex-input-button {
  display: flex;
  justify-content: space-between;
}
</style>
