<template>
  <a-card style="margin: 15px;">
    <a-tabs
      default-active-key="1"
      tab-position="left"
      @change="handleTabChange"
    >
      <a-tab-pane key="1" tab="风险辨识">
        <a-list
        >
          <a-list-item :key="index" v-for="(item, index) in contentData">
            <a-list-item-meta>
              <div slot="description">
                <span
                  style="margin-right: 5px;"
                  :class="item.value === 1 ? 'color-red' : 'color-green'">{{ item.value === 1 ? '有' : '不涉及' }}</span>
                <template v-if="item.type">
                  <span>安全措施:</span>
                  <span>{{ item.type }}</span>
                </template>
                <template v-else>
                  <span>{{ item.content }}</span>
                </template>
              </div>
              <div slot="title" class="font-weight-bold">
                <template v-if="item.type">
                  {{ parseInt(index) + 1 }}. {{ item.type }}
                </template>
                <template v-else>
                  {{ item.title }}
                </template>
              </div>
            </a-list-item-meta>
          </a-list-item>
        </a-list>
        <a-divider />
        <horizontal-list :list-data="signData"></horizontal-list>
      </a-tab-pane>
      <a-tab-pane key="2" tab="安全签字">
        <div class="measure-title">监护人签字</div>
        <signature-list :list-data="protoChargerSign"></signature-list>
        <a-divider class="measure-title-divider"/>
        <div class="measure-title">负责人签字</div>
        <signature-list :list-data="workChargerSign"></signature-list>
        <a-divider class="measure-title-divider"/>
        <div class="measure-title">作业人员签字</div>
        <signature-list :list-data="workersSign"></signature-list>
        <a-divider class="measure-title-divider"/>
        <horizontal-list :list-data="signData"></horizontal-list>
        <template v-if="presentImages.length > 0">
          <a-divider class="measure-title-divider"/>
          <div class="measure-title">
            现场图片
            <viewer :images="presentImages" class="present-img-viewer">
              <img v-for="src in presentImages" :key="src" :src="src" class="present-img">
            </viewer>
          </div>
        </template>
      </a-tab-pane>
      <a-tab-pane key="3" tab="安全交底">
        <safety-disclosure :content-data="riskFormData"></safety-disclosure>
        <a-divider />
        <horizontal-list :list-data="signData"></horizontal-list>
      </a-tab-pane>
    </a-tabs>
  </a-card>
</template>

<script>
import SignatureList from '../component/SignatureList'
import { hotWorkRiskFillinList } from './risks'
import { mapGetters } from 'vuex'
import HorizontalList from '../component/HorizontalList'
import SafetyDisclosure from '../component/SafetyDisclosure'
import cloneDeep from 'lodash.clonedeep'
export default {
  name: 'HighConfirmation',
  components: { HorizontalList, SafetyDisclosure, SignatureList },
  props: {
    ticketId: {
      type: String,
      default: '',
      required: true
    }
  },
  data () {
    return {
      contentData: Object.assign({}, hotWorkRiskFillinList),
      riskFormData: { riskType: [], measures: [] },
      ticketInfo: {},
      signData: {
        title1: '作业负责人',
        title2: '现场监护人'
      },
      presentImages: [],
      options: {
        navbar: false,
        fullscreen: false
      },
      protoChargerSign: [],
      workChargerSign: [],
      workersSign: []
    }
  },
  computed: {
     ...mapGetters(['userInfo'])
  },
  methods: {
    handleTabChange (tabIndex) {
      if (tabIndex === '1') {
        this.contentData = Object.assign({}, hotWorkRiskFillinList)
        this.signData = {
          title1: '作业负责人',
          title2: '现场监护人'
        }
        this.loadRiskDatas()
      } else if (tabIndex === '2') {
        this.signData = {
          title1: '作业单位安全教育人',
          title2: '所在单位安全教育人'
        }
        this.loadTicketDetail()
      } else if (tabIndex === '3') {
        this.contentData = { riskType: [], measures: [] }
        this.signData = {
          title1: '作业单位安全教育人',
          title2: '所在单位安全教育人'
        }
        this.loadSafefillin()
      }
    },
    loadRiskDatas () {
      const params = {
        id: this.ticketId,
        type: 0
      }
      this.getConfirmation(params)
    },
    getConfirmation (params, callback) {
      this.getList('/spang-safety/locale-confirm', params).then(({ data, code }) => {
        if (code === 200) {
					const content = data.content ? JSON.parse(data.content) : {}
					for (const key in this.contentData) {
						Object.assign(this.contentData[key], content[key])
					}
          this.signUsers = data.signUsers
					this.signUsers.map(item => {
						if (item.userType === 0) {
						  this.signData = Object.assign({}, this.signData, {
                // person1: item.userName,
                time1: item.signDate,
                sign1: item.signatureURL
              })
						}
						if (item.userType === 1) {
              this.signData = Object.assign({}, this.signData, {
                // person2: item.userName,
                time2: item.signDate,
                sign2: item.signatureURL
              })
						}
					})
          callback && callback(data)
        }
      })
    },
    loadTicketDetail () {
      const params = {
        id: this.ticketId,
        type: 2
      }
      this.getConfirmation(params, (data) => {
        this.presentImages = data.files.map(item => (this.BASE_URL + '/spang-safety/file' + item.filePath))
      })
      this.getById('/spang-safety/locale-confirm/sign', this.ticketId).then(({ data, code }) => {
        if (code === 200) {
          this.workChargerSign = data['1'].map(item => ({
            person: item.userName,
            time: item.signTime,
            sign: item.signatureUrl
          }))
          this.protoChargerSign = data['2'].map(item => ({
            person: item.userName,
            time: item.signTime,
            sign: item.signatureUrl
          }))
          this.workersSign = data['0'].map(item => ({
            person: item.userName,
            time: item.signTime,
            sign: item.signatureUrl
          }))
        }
      })
    },
    loadSafefillin () {
      const params = {
        id: this.ticketId,
        type: 4
      }
      this.getConfirmation(params, () => {
        this.contentData.riskType.forEach(item => {
          Object.assign(item, { fillIn: item.factor })
        })

        this.contentData.measures.forEach(item => {
          Object.assign(item, { fillIn: item.content, content: item.type })
        })
        this.riskFormData = cloneDeep(this.contentData)
      })
    }
  },
  created () {
    this.loadRiskDatas()
  }
}
</script>

