<template>
  <a-card style="margin: 15px;">
    <a-tabs
      default-active-key="1"
      tab-position="left"
      @change="handleTabChange"
    >
      <a-tab-pane key="1" tab="风险辨识">
        <a-list
          class="demo-loadmore-list"
          item-layout="horizontal"
          :data-source="riskDatas"
        >
          <a-list-item slot="renderItem" slot-scope="item, index">
            <a slot="actions" v-if="false">查看图片</a>
            <a-list-item-meta>
              <div slot="description">
                <span
                  style="margin-right: 5px;"
                  :class="item.isTitle === 0 ? 'color-red' : 'color-green'">{{ item.isTitle === 0 ? '有' : '不涉及' }}</span>
              </div>
              <div slot="title" class="font-weight-bold">
                {{ index + 1 }}.
                <label-with-input :label="item.label" :label-values="item.titleValue.split(',')"/>
              </div>
            </a-list-item-meta>

            <div class="list-content">
              <div class="list-content-item">
                <span style="width: 150px; display: inline-block">{{ item.signDate }}</span>
                <sign-img style="margin-left: 15px; display: inline-block;" :src="item.signPicUrl"></sign-img>

              </div>

            </div>

          </a-list-item>
        </a-list>
        <horizontal-list :list-data="signData"></horizontal-list>
      </a-tab-pane>
      <a-tab-pane key="2" tab="安全签字">
        <signature-list :list-data="signatureList" />
        <template v-if="presentImages.length > 0">
          <a-divider/>
          <div class="measure-title">
            现场图片
            <viewer :images="presentImages" class="present-img-viewer">
              <img v-for="src in presentImages" :key="src" :src="src" class="present-img">
            </viewer>
          </div>
        </template>
      </a-tab-pane>
      <a-tab-pane key="3" tab="安全交底">
        <safety-disclosure :content-data="safetyForm"></safety-disclosure>
        <horizontal-list :list-data="signData"></horizontal-list>
      </a-tab-pane>
    </a-tabs>
  </a-card>
</template>

<script>
import { hotWorkRiskFillinList } from './risks'
import { safetyForm } from '../component/risk'
import { getRiskIdentificationDetail, getDetail, getSafetyFromDetail } from '@/api/hoisting-work'
import LabelWithInput from '../../ticket/component/LabelWithInput'
import { mapGetters } from 'vuex'
import SignImg from '../component/SignImg'
import HorizontalList from '../component/HorizontalList'
import SafetyDisclosure from '../component/SafetyDisclosure'
import SignatureList from '../component/SignatureList'
import cloneDeep from 'lodash.clonedeep'
export default {
  name: 'Confirmation',
  components: { LabelWithInput, SignImg, HorizontalList, SafetyDisclosure, SignatureList },
  props: {
    ticketId: {
      type: String,
      default: '',
      required: true
    }
  },
  data () {
    return {
      signatureList: [
        {title: '作业单位安全教育人'},
        {title: '所在单位安全教育人'},
        {title: '施工方现场负责人'},
        {title: '施工方机械指挥人'},
        {title: '施工方项目总负责人'}
      ],
      riskDatas: hotWorkRiskFillinList,
      safetyForm: cloneDeep(safetyForm),
      signData: {
        title1: '所在车间安全措施落实人'
      },
      presentImages: [],
    }
  },
  computed: {
     ...mapGetters(['userInfo'])
  },
  methods: {
    handleTabChange (tabIndex) {
      if (tabIndex === '1') {
        this.signData = {
          title1: '所在车间安全措施落实人'
        }
        this.loadRiskDatas()
      } else if (tabIndex === '2') {
        this.loadTicketDetail()
      } else if (tabIndex === '3') {
        this.signData = {
          title1: '作业单位安全教育人',
          title2: '所在单位安全教育人'
        }
        this.loadSafefillin()
      }
    },
    loadRiskDatas () {
      const params = {
        id: this.ticketId,
        type: 0
      }
      this.getList('/spang-safety/locale-confirm', params).then(({ data, code }) => {
        if (code === 200) {
          const content = data.content ? JSON.parse(data.content) : {}
          for (const key in this.contentData) {
            Object.assign(this.contentData[key], content[key])
          }
          this.signUsers = data.signUsers;
          data.signUsers.map(item => {
            if (item.userType === 0) {
              this.$set(this.signData, 'time1', item.signDate)
              this.$set(this.signData, 'person1', item.userName)
              this.$set(this.signData, 'sign1', item.signatureURL ? item.signatureURL : '')
            }
          })
        }
      })
    },
    loadTicketDetail () {
      const params = {
        id: this.ticketId,
        type: 2
      }
      this.getList('/spang-safety/locale-confirm', params).then(({ data, code }) => {
        data.signUsers.forEach(item => {
          if (item.userType === 0) {
            this.$set(this.signatureList[0], 'time', item.signDate)
            this.$set(this.signatureList[0], 'person', item.userName)
            this.$set(this.signatureList[0], 'sign', item.signatureURL ? item.signatureURL : '')
          }
          if (item.userType === 1) {
            this.$set(this.signatureList[1], 'time', item.signDate)
            this.$set(this.signatureList[1], 'person', item.userName)
            this.$set(this.signatureList[1], 'sign', item.signatureURL ? item.signatureURL : '')
          }
        })
        this.presentImages = data.files.map(item => (this.BASE_URL + '/spang-safety/file' + item.filePath))
      })
      this.getById('/spang-safety/locale-confirm/sign', this.ticketId).then(({ data, code }) => {
        if (code === 200) {
          const signData5 = data['5'] && data['5'].length > 0 ? data['5'][0] : {}
          this.$set(this.signatureList[2], 'time', signData5.signTime)
          this.$set(this.signatureList[2], 'person', signData5.userName)
          this.$set(this.signatureList[2], 'sign', signData5.signatureUrl ? signData5.signatureUrl : '')
          const signData7 = data['7'] && data['7'].length > 0 ? data['7'][0] : {}
          this.$set(this.signatureList[3], 'time', signData7.signTime)
          this.$set(this.signatureList[3], 'person', signData7.userName)
          this.$set(this.signatureList[3], 'sign', signData7.signatureUrl ? signData7.signatureUrl : '')
          const signData6 = data['6'] && data['6'].length > 0 ? data['6'][0] : {}
          this.$set(this.signatureList[4], 'time', signData6.signTime)
          this.$set(this.signatureList[4], 'person', signData6.userName)
          this.$set(this.signatureList[4], 'sign', signData6.signatureUrl ? signData6.signatureUrl : '')
          if(data['8']){
            const signData8 = data['8'] && data['8'].length > 0 ? data['8'][0] : {}
            this.$set(this.signatureList[6], 'title', '现场监护人')
            this.$set(this.signatureList[5], 'time', signData8.signTime)
            this.$set(this.signatureList[5], 'person', signData8.userName)
            this.$set(this.signatureList[5], 'sign', signData8.signatureUrl ? signData8.signatureUrl : '')
          }
          if(data['9']){
            const signData9 = data['9'] && data['9'].length > 0 ? data['9'][0] : {}
            this.$set(this.signatureList[6], 'title', '项目所在车间(部门)现场安全措施落实人')
            this.$set(this.signatureList[6], 'time', signData9.signTime)
            this.$set(this.signatureList[6], 'person', signData9.userName)
            this.$set(this.signatureList[6], 'sign', signData9.signatureUrl ? signData9.signatureUrl : '')
          }
        }
      })
    },
    loadSafefillin () {
      const params = {
        id: this.ticketId,
        type: 4
      }
      this.getList('/spang-safety/locale-confirm', params).then(({ data, code }) => {
        this.safetyForm = cloneDeep(this.contentData)
        data.signUsers.forEach(item => {
          if (item.userType === 0) {
            this.$set(this.signData, 'time1', item.signDate)
            this.$set(this.signData, 'person1', item.userName)
            this.$set(this.signData, 'sign1', item.signatureURL ? item.signatureURL : '')
          }
          if (item.userType === 1) {
            this.$set(this.signData, 'time2', item.signDate)
            this.$set(this.signData, 'person2', item.userName)
            this.$set(this.signData, 'sign2', item.signatureURL ? item.signatureURL : '')
          }
        })
      })
    }
  },
  created () {
    this.loadRiskDatas()
  }
}
</script>

<style scoped lang="less">
  .color-red {
    color: #F26161;
  }

  .color-green {
    color: #10B24C;
  }
  .font-weight-bold {
    font-size: 14px;
    font-weight: 600;
  }
  .flex-row {
    display: flex;
    .el-col {
      float: none;
    }
  }

  .measure-content-desc {
    display: inline-flex;
  }
  .measure-content-item {
    display: inline-block;
    margin-right: 10px;
    line-height: 1.4;
  }

</style>
