<template>
  <div style="display: inline">
    <template v-for="(title, titleIndex) in label.split('$')">

      {{ title }}
      <template v-if="labelValues && label.endsWith('$') && titleIndex < label.split('$').length - 1">
        ({{ labelValues[titleIndex] }})
      </template>
      <template v-else-if="labelValues && titleIndex < label.split('$').length - 1">
        ({{ labelValues[titleIndex] }})
      </template>
    </template>
  </div>

</template>

<script>
export default {
  name: 'LabelWithInput',
  props: {
    label: {
      type: String,
      default: '',
      required: true
    },
    labelValues: {
      type: Array,
      default: () => [],
      required: false
    }

  }
}
</script>

