<template>
  <page-container :title="false">
    <a-card :bordered="false">
      <div class="table-page-search-wrapper">
        <a-form layout="inline">
          <a-row :gutter="24">
            <a-col :md="5">
              <!--              <department-select label-name="申请单位" v-model="queryParam.applyUnitId"/>-->
              <a-form-item label="申请单位">
                <a-tree-select
                  v-model="queryParam.applyUnitId"
                  style="width: 100%;"
                  :dropdownStyle="{ maxHeight:'35vh', overflow: 'auto'}"
                  :tree-data="treeData"
                  placeholder="请选择单位"
                />
              </a-form-item>
            </a-col>
            <a-col :md="6">
              <a-form-item label="申请人">
                <person-select :decorator-name="queryParam.personId"/>
              </a-form-item>
            </a-col>
            <a-col :md="7">
              <a-form-item label="作业时间">
                <a-range-picker valueFormat="YYYY-MM-DD HH:mm:ss" v-model="queryParam.time" showTime/>
              </a-form-item>
            </a-col>
            <a-col :md="2">
              <span class="table-page-search-submitButtons">
                <a-button type="primary" @click="researchRecord">查询</a-button>
                <a-button type="second" @click="resetRecord" style="margin-left: 10px">重置</a-button>
              </span>
            </a-col>
          </a-row>
          <a-row :gutter="24">
            <a-col :md="5">
              <a-form-item label="作业类别">
                <a-select v-model="queryParam.ticketType">
                  <a-select-option v-for="item in ticketTypeOptions" :key="item.dictKey">
                    {{ item.dictValue }}
                  </a-select-option>
                </a-select>
              </a-form-item>
            </a-col>
            <a-col :span="6">
              <a-form-item label="流程状态">
                <a-select v-model="queryParam.processStatus">
                  <a-select-option v-for="item in ticketStatus" :key="item.dictKey">
                    {{ item.dictValue }}
                  </a-select-option>
                </a-select>
              </a-form-item>
            </a-col>
            <a-col :span="7">
              <a-form-item label="作业证编号">
                <a-input v-model="queryParam.taskNo"/>
              </a-form-item>
            </a-col>
          </a-row>
        </a-form>
      </div>
      <div class="table-operator">
        <a-button type="primary" icon="plus" @click="addRecord">新增</a-button>
        <a-button icon="delete" @click="removeRecords" :disabled="!hasSelected" v-if="false">删除</a-button>
        <!-- <a-button icon="download" :disabled="!hasSelected" @click="fileDownload">导出当前</a-button>
        <a-button icon="download" @click="filesDownload">导出所有</a-button>
        <a-button icon="upload" @click="fileUpload">导入</a-button> -->
        <a-radio-group button-style="solid" v-model="tableType" @change="$refs.table.refresh(true)" style="float: right">
          <a-radio-button value="all">查看全部</a-radio-button>
          <a-radio-button value="todo">待办任务</a-radio-button>
        </a-radio-group>
      </div>
      <s-table
        ref="table"
        size="default"
        :rowKey="record => record.id"
        :columns="columns"
        :data="loadData"
        :alert="false"
        :scroll="{y:`calc(100vh - 360px)`}"
        showPagination="true"
      >
        <span slot="serial" slot-scope="text, record, index">
          {{ index + 1 }}
        </span>
        <span slot="time" slot-scope="text, record">
          {{ record.startTime }}&nbsp;至&nbsp;{{ record.endTime }}
        </span>
        <span slot="processStatus" slot-scope="text, record">
          <a-tag :color="record.status===2 || record.status===5?'red':'green'">{{ text }}</a-tag>
        </span>
        <span slot="action1" slot-scope="text, record, index" style="margin-right: 15px;">
          <a @click="taskCheck(record)">查看</a>
        </span>
        <span slot="action2" slot-scope="text, record, index">
          <a @click="taskApprove(record, index)" v-if="[0].includes(record.status)">审批</a>
          <a-divider type="vertical" v-if="[0].includes(record.status)"/>
          <a @click="taskRevoke(record, index)" v-if="[2].includes(record.status)">撤回</a>
          <a-divider type="vertical" v-if="[2].includes(record.status)"/>
          <a @click="taskEdit(record, index)" v-if="[2].includes(record.status)">编辑</a>
          <a-divider type="vertical" v-if="[2].includes(record.status)"/>
          <a @click="taskCheck(record)">查看</a>
          <a-divider type="vertical"/>
          <a @click="flowchartShow(record)">流程示意</a>
        </span>
      </s-table>
      <ticket-modal
        ref="modal"
        :visible="commonVisible"
        :loading="confirmLoading"
        :model="commonData"
        :mode="commonMode"
        :ticket-type-options="ticketTypeOptions"
        :modal-title="commonTitle"
        :disabled="commonDisabled"
        @cancel="modalCancel"
        @ok="modalOk"
      />
      <approve-modal
        ref="approveModal"
        :visible="approveVisible"
        @cancel="approveCancel"
        @ok="approveOk"
      >
      </approve-modal>
      <detail-modal
        ref="detailModal"
        :visible="detailVisible"
        :basic-info="detailBasicInfo"
        :ticket-info="detailTicketInfo"
        @cancel="detailCancel"
      />
      <flow-chart
        ref="flowchart"
        :model="processDataList[processDataIndex]"
        :visible="flowChartVisible"
        @cancel="flowchartCancel"
      ></flow-chart>
    </a-card>
  </page-container>
</template>
<script>
import { STable } from '@/components'
import { ticketColumns } from '@/views/safety/table'
import TicketModal from '@/views/safety/ticket/component/TicketModal'
import { task, getTaskType, getTaskStatus, getPersonnelTree } from '@/api/safety'
import ApproveModal from '@/views/safety/schedule/component/ApproveModal'
import PersonnelModal from '@/views/safety/organizations/component/PersonnelModal'
import PersonSelect from '@/views/safety/components/PersonSelect'
import DepartmentSelect from '@/views/safety/components/DepartmentSelect'
import DetailModal from '@/views/safety/ticket/component/DetailModal'
import FlowChart from '@/views/safety/schedule/component/FlowChart'
import { mapGetters } from 'vuex'

// const SHOW_PARENT = TreeSelect.SHOW_PARENT
const ticketDict = { 0: 'taskHotWorkTicketDTO',
1: 'taskConfinedSpaceTicketDTO',
2: 'taskBlindWorkTicketDTO',
3: 'taskHighWorkTicketDTO',
  4: 'taskHoistingWorkTicketDTO',
5: 'taskElectricityTicketDTO',
6: 'taskSoilWorkTicketDTO',
7: 'taskOpenCircuitTicketDTO' }

export default {
  name: 'Ticket',
  components: {
    PersonnelModal,
    STable,
    TicketModal,
    ApproveModal,
    DepartmentSelect,
    PersonSelect,
    DetailModal,
    FlowChart
    // TreeSelect
  },
  data () {
    return {
      // modal
      confirmLoading: false,
      commonTitle: '',
      commonVisible: false,
      commonData: null,
      commonMode: true, // add: true  edit: false
      commonDisabled: false,
      // selectOptions: [],
      queryParam: {},
      loadData: parameter => {
        const requestParameters = { current: parameter.pageNo, size: parameter.pageSize }
        Object.assign(requestParameters, this.queryParam)
        if (this.tableType === 'all') {
          return task.getTaskList(requestParameters).then(res => {
            this.columns.pop()
            this.columns.push({
              title: '操作',
              dataIndex: 'action1',
              width: '120px',
              align: 'center',
              scopedSlots: { customRender: 'action1' }
            })
            return {
              totalCount: res.data.total,
              totalPage: res.data.pages,
              pageNo: res.data.current,
              pageSize: res.data.size,
              data: res.data.records
            }
          })
        } else {
          return task.getTaskTodoList(requestParameters).then(res => {
            if (Object.keys(res.data).length === 0) {
              return { data: [] }
            }
            this.columns.pop()
            this.columns.push({
              title: '操作',
              dataIndex: 'action2',
              width: '220px',
              align: 'center',
              scopedSlots: { customRender: 'action2' }
            })
            this.processDataList = res.data.records
            return {
              totalCount: res.data.total,
              totalPage: res.data.pages,
              pageNo: res.data.current,
              pageSize: res.data.size,
              data: res.data.records
            }
          })
        }
      },
      selectedRowKeys: [],
      selectedRows: [],
      // select options
      ticketTypeOptions: [],
      tableType: 'all',
      ticketStatus: [],
      treeData: [],
      // approve
      approveVisible: false,
      // detail modal
      detailVisible: false,
      detailBasicInfo: {},
      detailTicketInfo: {},
      // flowchart
      processDataList: [], // 记录工作流相关信息
      processDataIndex: 0, // 记录要操作的工作流index
      flowChartVisible: false
    }
  },
  created () {
    this.getSelectOptions()
  },
  computed: {
    ...mapGetters(['userInfo']),
    columns: () => ticketColumns,
    hasSelected () {
      return this.selectedRowKeys.length > 0
    },
    rowSelection () {
      return {
        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange
      }
    }
  },
  methods: {
    commonInit () {
      this.commonData = {}
      this.commonVisible = false
      this.commonDisabled = false
      this.commonTitle = ''
      this.commonMode = true
      this.confirmLoading = false
      const form = this.$refs.modal.form
      form.resetFields()
    },
    getSelectOptions () {
      getTaskType().then(res => {
        this.ticketTypeOptions = res.data
    })
      getTaskStatus().then(res => {
        this.ticketStatus = res.data
      })
      getPersonnelTree(0).then(res => {
        this.treeData = res.data
      })
    },
    onSelectChange (selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    researchRecord () {
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    resetRecord () {
      this.queryParam = {}
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    addRecord () {
      this.commonTitle = '新增数据'
      this.commonMode = true
      this.commonVisible = true
    },
    removeRecords () {
      var ids = ''
      this.selectedRows.forEach((item, index) => {
        ids = ids + item.id + ','
      })
      this.showDeleteConfirm(ids)
    },
    removeRecord (record) {
      this.showDeleteConfirm(record.id)
    },
    taskCheck (record) {
      // this.commonTitle = '查看数据'
      // this.commonData = { ...record }
      // this.commonVisible = true
      // this.commonDisabled = true
      const requestForm = {
        id: record.id,
        type: record.ticketTypeNum,
        taskId: record.taskId || ''
      }
      task.detailTaskList(requestForm).then(res => {
        // 测试
          const result = {
            taskTicketVO:
              {
                id: '1',
                ticketType: 0,
                applyUnit: 'GB 18218-2018',
                applyPerson: 'admin',
                taskContent: '文件有效性',
                processStatus: '通过',
                status: 2,
                ticketTypeNum: 0,
                ticketTypeName: '动火作业票',
                taskNo: 'GC00120190521002',
                level: 4 // 作业进行到第几步了
              }
          }
          res.data = result
        // 测试结束
        this.detailBasicInfo = { ...res.data.taskTicketBaseDetailVO }
        this.detailTicketInfo = { ...res.data.taskTicketVO }
        this.detailVisible = true
      })
    },
    taskEdit (record, index) {
      const requestForm = {
        id: record.id,
        type: record.ticketTypeNum,
        taskId: record.taskId
      }
      task.detailTaskList(requestForm).then(res => {
        this.commonTitle = '编辑'
        this.commonData = { ...res.data }
        this.processDataIndex = index
        this.commonMode = false
        this.commonVisible = true
      })
    },
    fileDownload () {
      this.$message.success('导出当前文件')
    },
    filesDownload () {
      this.$message.success('导出所有文件')
    },
    fileUpload () {
      // this.$message.success('编辑')
    },
    modalCancel () {
      this.commonInit()
    },
    modalOk () {
      const _this = this
      if (_this.commonDisabled) {
        _this.$message.success('查看返回')
        _this.commonInit()
        return
      }
      const form = _this.$refs.modal.form
      form.validateFields((errors, values) => {
        if (!errors) {
          if (!_this.commonMode) {
            // 编辑
            const ticketId = ticketDict[values.ticketType]
            values.id = this.commonData.taskTicketBaseDetailVO.id
            values[ticketId].id = this.commonData.taskTicketVO.id
            const params = {
              processDefinitionKey: 'task_ticket', // 工作流字段
              taskTicketDTO: values,
              variables: { taskTicketStatus: 3 }
            }
            const processData = this.processDataList[this.processDataIndex]
            params.businessId = processData.id
            params.taskId = processData.taskId
            params.processInstanceId = processData.processInstanceId
            params.comment = ''
            console.log('表单数据', params)
            task.approveTask(params).then(res => {
              if (res.code === 200) {
                _this.$message.success(`${res.msg}`)
                _this.commonInit()
                _this.$refs.table.refresh(true)
                _this.$refs.table.clearSelected()
              } else {
                _this.$message.error(`${res.msg}`)
              }
            })
          } else {
            // 新增作业票工作流
            const requestForm = {
              processDefinitionKey: 'task_ticket', // 工作流字段
              taskTicketDTO: values,
              approverId: values.approverId
            }
            task.submitTaskList(requestForm).then(res => {
              if (res.code === 200) {
                _this.$message.success(`${res.msg}`)
                _this.commonInit()
                _this.$refs.table.refresh(true)
                _this.$refs.table.clearSelected()
              } else {
                _this.$message.error(`${res.msg}`)
              }
            })
          }
        }
      })
    },
    showDeleteConfirm (params) {
      const _this = this
      this.$confirm({
        title: '确定执行删除操作?',
        content: '',
        okText: '是',
        okType: 'danger',
        cancelText: '否',
        onOk () {
          task.removeTaskList(params).then(res => {
            if (res.code === 200) {
              _this.$message.success('删除成功')
              _this.$refs.table.refresh(true)
              _this.$refs.table.clearSelected()
            } else {
              _this.$message.success('删除失败')
            }
          })
        },
        onCancel () {
          console.log('Cancel')
        }
      })
    },
    taskApprove (record, index) {
      this.processDataIndex = index
      // this.commonData = { ...record }
      this.approveVisible = true
    },
    approveCancel () {
      this.$refs.approveModal.form.resetFields()
      this.approveVisible = false
    },
    approveOk () {
      const _this = this
      const form = this.$refs.approveModal.form
      form.validateFields((errors, values) => {
        if (!errors) {
          console.log(values)
          const params = {}
          const processData = this.processDataList[this.processDataIndex]
          params.businessId = processData.id
          params.taskId = processData.taskId
          params.processInstanceId = processData.processInstanceId
          params.comment = values.comment
          params.variables = { taskTicketStatus: Number(values.status) }
          console.log(params)
          task.approveTask(params).then(res => {
            if (res.code === 200) {
              _this.$message.success(`${res.msg}`)
              _this.$refs.table.refresh(true)
              _this.$refs.table.clearSelected()
              _this.$refs.approveModal.form.resetFields()
              _this.approveVisible = false
            } else {
              _this.$message.error(`${res.msg}`)
            }
          })
        }
      })
    },
    detailCancel () {
      this.detailVisible = false
      setTimeout(() => {
        this.detailBasicInfo = {}
        this.detailTicketInfo = {}
      }, 500)
    },
    flowchartShow (record) {
      this.flowChartVisible = true
      this.processData = record
    },
    taskRevoke (record, index) {
      const _this = this
      const processData = this.processDataList[index]
      this.$confirm({
        title: '提示',
        content: '将撤回该作业票申请，是否确认？',
        okText: '确定',
        okType: 'danger',
        cancelText: '取消',
        onOk () {
          const params = {
            businessId: processData.id,
            taskId: processData.taskId,
            processInstanceId: processData.processInstanceId,
            comment: '',
            variables: { taskTicketStatus: 4 }
          }
          task.revokeTask(params).then(res => {
            if (res.code === 200) {
              _this.$message.success(`${res.msg}`)
              _this.$refs.table.refresh(true)
              _this.$refs.table.clearSelected()
            } else {
              _this.$message.error(`${res.msg}`)
            }
          })
        }
      })
    },
    flowchartCancel () {
      this.flowChartVisible = false
    }
  }
}
</script>
<style lang="less" scoped>
/deep/ .ant-card-body {
  .ant-table-wrapper {
    min-height: calc(100vh - 280px);
  }
}
/deep/ .table-page-search-wrapper {
  .ant-form-inline {
    .ant-form-item > .ant-form-item-label {
      width: 100px;
    }
  }

}
</style>
